    $(function() {
    	CreativeTemplate.Init();
    });

    var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "ExtraRSItems" : "",

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.MyStart();
            this.Header();
            this.ChannelBar();
            this.Body();
            this.Footer();
            this.Slideshow();
            this.ModEvents();
            this.PageListMenu();           
            this.Search();

            $(window).load(function(){ _this.WindowLoad(); _this.MyStartOverride(); });
            $(window).resize(function(){ _this.WindowResize(); });    
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {
            this.CalendarListView();
            $("div.ui-widget.app.about-teacher").each(function(){
            	if($("#about-teacher-information", this).height() == 0) {
                	$("#about-teacher-information", this).hide();
                }
            });
            // MAKE NAVIGATION APPS ACCESSIBLE
            $(".ui-widget.app.navigation").csAccessibleNavigationApp();
        },

        "WindowResize": function() {
            this.CalendarListView();
            var getWidth = $(window).width();
            if(getWidth <= 768 && $("#sw-content-container10.region.ui-hp .rs-gallery-container").length) {
                //DOC se height on mmr desc to keep content from jumping
                var scrolled = 0;
                var scrollLock = 0;
                var loc = $("#hp-slideshow-outer").outerHeight();
                $(window).scroll(function(e) {
                    scrolled = $(window).scrollTop();
                    if(scrolled >= loc && scrollLock === 0) {
                        $("#hp-slideshow .rs-gallery-desc").height($("#hp-slideshow .rs-gallery-desc").height());
                        scrollLock = 1;
                    scrollLock = 1;
                    } else if(scrolled < loc && scrollLock == 1){
                        $("#hp-slideshow .rs-gallery-desc").height("auto");
                        scrollLock = 0;
                    }
                });
            }
        },

        "MyStart": function() {
        	// FOR SCOPE
            var _this = this;

            $(document).off("click", "#sw-mystart-account");

            $(document).on("click", ".sw-mystart-dropdown", function(e) {
                $(".sw-dropdown", this).slideDown(300, "swing");
                $(this).addClass("open");
            });
            $(".sw-mystart-dropdown").hover(function(e) {
                e.preventDefault();
            }, function() {
                $(".sw-dropdown",this).slideUp(300, "swing").attr('aria-hidden', 'true');
                $(this).removeClass("open");
            });
            $(document).off("focus", ".sw-mystart-dropdown");
            $(document).on("focus", ".sw-mystart-dropdown", function(e) {
                $(".sw-dropdown", this).slideDown(300, "swing").attr('aria-hidden', 'false');
                $(this).addClass("open");
            });

            $("#gb-mystart #sw-myaccount").text("User Options");
            $("#ui-btn-mypasskey span").text("Passkeys");
            if($("#sw-mystart-mypasskey").length) {
            	$("#sw-mystart-mypasskey").prependTo($("#gb-mystart-right-inner"));
            }
            if($(".sw-mystart-button.pw").length) {
            	$(".sw-mystart-button.pw a").appendTo($("#gb-mystart #sw-myaccount-list")).wrap("
");
                $("#gb-mystart #sw-myaccount-list li").last().appendTo($("#gb-mystart #sw-myaccount-list"));
            }
            if($(".sw-mystart-button.manage").length) {
            	$("#gb-mystart .sw-mystart-button.manage a").prependTo($("#gb-mystart #sw-myaccount-list")).wrap("
");
            }
            if($(".sw-mystart-button.signin").length) {
            	$("#gb-mystart .sw-mystart-button.signin a span").text("Staff");
            }
            /* added by Matt - case#3142315 - 05.08.18 */
            if($(".sw-mystart-button.signin").length) {
            	$("#gb-mystart .sw-mystart-button.signin a").attr("href","https://www.west-fargo.k12.nd.us/domain/545");
            }
            if("wfps" != "wfps") {
            	$(".sw-mystart-button.home").prependTo($("#gb-mystart-right-inner"));
            }
            $("#gb-mystart #sw-myaccount-list li").each(function() {
            	if($("a span", this).text() == "Edit Account Settings") {
                	$("a span", this).text("My Account");
                } else if($("a span", this).text() == "Sign Out") {
                	$(this).appendTo($("#gb-mystart #sw-myaccount-list"));
                }
            });

            $("#gb-mystart #sw-myaccount-list a").each(function() {
            	if($("a span", this).text() == "Edit Account Settings") {
                	$("a span", this).text("My Account");
                } else if($("a span", this).text() == "Sign Out") {
                	$(this).appendTo($("#gb-mystart #sw-myaccount-list"));
                }
            });

            $("#sw-mystart-outer").remove();
        },

        "MyStartOverride": function() {
            // FOR SCOPE
            var _this = this;

            // OVERRIDE SYSTEM MY ACCOUNT DROPDOWN
            var top_level_nav = $('.sw-mystart-nav');
            var myaccountlinks = $(top_level_nav).next('ul').find('a');
            myaccountlinks.off();
            bindMyStartBarLinks(myaccountlinks);

            function bindMyStartBarLinks(links) {
                $(links).keydown(function (e) {
                    e.stopPropagation();

                    if (e.keyCode == 38) { //key up
                        e.preventDefault();

                        // This is the first item
                        if ($(this).parent('li').prev('li').length == 0) {
                            if ($(this).parents('ul').parents('.sw-mystart-nav').length > 0) {
                                $(this).parents('ul').parents('.sw-mystart-nav').focus();
                            } else {
                                $(this).parents('ul').prev('.sw-mystart-nav').focus();
                            }
                        } else {
                            $(this).parent('li').prev('li').find('a').first().focus();
                        }
                    } else if (e.keyCode == 40) { //key down
                        e.preventDefault();

                        if ($(this).parent('li').next('li').length == 0) {
                            if ($(this).parents('ul').parents('.sw-mystart-nav').length > 0) {
                                $(this).parents('ul').parents('.sw-mystart-nav').focus();
                            } else {
                                $(this).parents('ul').prev('.sw-mystart-nav').focus();
                            }
                        } else {
                            $(this).parent('li').next('li').find('a').first().attr('tabIndex', 0);
                            $(this).parent('li').next('li').find('a').first().focus();
                        }
                    } else if (e.keyCode == 27 || e.keyCode == 37) { // escape key or key left
                        e.preventDefault();
                        hideMyStartBarMenu();
                    } else if (e.keyCode == 32) { //enter key
                        e.preventDefault();
                        window.location = $(this).attr('href');
                    } else {
                        var found = false;

                        $(this).parent('div').nextAll('li').find('a').each(function () {
                            if ($(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                                $(this).focus();
                                found = true;
                                return false;
                            }
                        });

                        if (!found) {
                            $(this).parent('div').prevAll('li').find('a').each(function () {
                                if ($(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                                    $(this).focus();
                                    return false;
                                }
                            });
                        }
                    }
                });
            }            
        },

        "Header": function() {
        	// FOR SCOPE
            var _this = this;

            $("#gb-header-right p").each(function() {
            	if($(this).text().indexOf("@") != -1) {
                	$(this).html(''+$(this).text()+'');
                }
            });
            if($("#gb-header-right p").height() == 0) {
            	$("#gb-header-right").addClass("hide768");
            }

         	$.schoolListMinibase({
              'miniBaseDomainID': 4,
              'miniBaseModuleInstanceID': 1304,
              'cache': true,
              'schoolListReady': function(schoolList) {     
                /*
                The returned value is an array of objects and each object is a school record.
                The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
                */
                var schoolsType = [];
                for(var i in schoolList) {
                    if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
						if(schoolsType.indexOf(schoolList[i].schoolType) == -1) {
                        	schoolsType.push(schoolList[i].schoolType);
                            $("#gb-schools-tabs").append(''+schoolList[i].schoolType+'');
                            $("#gb-schools-menus").append('');
                        }
                    }
                }
                for(var i in schoolList) {
                    if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                    	if(schoolsType.indexOf(schoolList[i].schoolType) !== -1) {
                        	var rsMenuIndex = $(".gb-schools-menu:eq("+schoolsType.indexOf(schoolList[i].schoolType)+")").index()+1;
                        	$(".gb-schools-menu").eq(schoolsType.indexOf(schoolList[i].schoolType)).append('
' + schoolList[i].schoolName + '');
       						CreativeTemplate.ExtraRSItems += rsMenuIndex+","+schoolList[i].schoolName+","+schoolList[i].schoolUrl+",_self;";
                        }
                    }
                }

				CreativeTemplate.ExtraRSItems += "5,District Home,/,_self;"; //added District Home link by Lyndsay - Case 00528628 - 4.28.17

                $(".gb-schools-tab").eq(0).addClass("active").attr("aria-expanded", "true");
                $(".gb-schools-menu").eq(0).addClass("active").attr("aria-hidden", "false");
                $(".gb-schools-menu:eq(0) a").attr("tabindex", "0");

                $(document).on("click keydown", ".gb-schools-tab", function(e) {
                    if(_this.A11yClick(e)) {
                        e.preventDefault();
                        $(".gb-schools-tab").removeClass("active").attr("aria-expanded", "false");
                		$(".gb-schools-menu").removeClass("active").attr("aria-hidden", "true");
                        $(".gb-schools-menu:eq(0) a").attr("tabindex", "-");
                        $(this).addClass("active").attr("aria-expanded", "true");
                		$(".gb-schools-menu").eq($(this).index()).addClass("active").attr("aria-hidden", "false");
                        $(".gb-schools-menu:eq("+$(this).index()+") a").attr("tabindex", "0");
                        $(".gb-schools-menu:eq("+$(this).index()+") li:first a").focus();
                    } else {
        				handleTabListKey($(this), ".gb-schools-tab", e);
                    }
                }); 

                _this.RsMenu();

              }
        	});

