            // FOCUS LEAVES SCHOOL MENU
            $(document).on("keydown", "#gb-schools-container a", function(e) {
                // GIVE THE DOCUMENT TIME TO FOCUS THE NEXT ITEM
                setTimeout(function() {
                    // IF THIS DROPDOWN OR ANY OF ITS CHILDREN DO NOT HAVE FOCUS
                    if(!$("#gb-schools-container").find(":focus").length && !$("#gb-schools-btn").is(":focus")) {
                        // CLOSE THE MENU
                    	$("#gb-schools-container").attr("aria-hidden", "true").slideUp(500);
                        $("#gb-schools-btn").removeClass("active").attr("aria-expanded", "false").focus();
                    }
                }, 100);
            });

           $(document).on("keydown", "#gb-translate-container ul a", function(event) {
             	var lastIndex = $("#gb-translate-container ul a").length - 1;
                switch(event.keyCode){
                    // CONSUME LEFT AND UP ARROWS
                    case _this.KeyCodes.left:
                    case _this.KeyCodes.up:
                        event.preventDefault();
                        // FOCUS THE PREVIOUS TAB
                        if ($(this).parent().index() == 0) { 
                            $("#gb-translate-container ul li:last-child a").focus();
                        } else {
                         	$("#gb-translate-container ul li:eq("+($(this).parent().index()-1)+") a").focus();
                        } 
                    break;
                    // CONSUME RIGHT AND DOWN ARROWS
                    case _this.KeyCodes.right:
                    case _this.KeyCodes.down:
                        event.preventDefault();
                        // FOCUS THE NEXT TAB
                        if ($(this).index() == lastIndex) { 
                        	$("#gb-translate-container ul li:first-child a").focus();
                        } else {
                            $("#gb-translate-container ul li:eq("+($(this).parent().index()+1)+") a").focus();
                        }
                    break;
                  }
            }); 

            // FOCUS LEAVES TRANSLATE MENU
            $(document).on("keydown", "#gb-translate-container a", function(e) {
                // GIVE THE DOCUMENT TIME TO FOCUS THE NEXT ITEM
                setTimeout(function() {
                    // IF THIS DROPDOWN OR ANY OF ITS CHILDREN DO NOT HAVE FOCUS
                    if(!$("#gb-translate-container").find(":focus").length && !$("#gb-translate-btn").is(":focus")) {
                        // CLOSE THE MENU
                    	$("#gb-translate-container").attr("aria-hidden", "true").slideUp(500);
                        $("#gb-translate-btn").removeClass("active").attr("aria-expanded", "false").focus();
                    }
                }, 100);
            });

            function handleTabListKey(tab, tabClass, event) {
                var lastIndex = $(tabClass).length - 1;

                switch(event.keyCode){
                    // CONSUME LEFT AND UP ARROWS
                    case _this.KeyCodes.left:
                    case _this.KeyCodes.up:
                        event.preventDefault();

                        // FOCUS THE PREVIOUS TAB
                        if (tab.index() == 0) { 
                            $(tabClass + ":last-child").focus();
                        } else { 
                            $(tabClass).eq(tab.index() - 1).focus();
                        } 
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case _this.KeyCodes.right:
                    case _this.KeyCodes.down:
                        event.preventDefault();

                        // FOCUS THE NEXT TAB
                        if (tab.index() == lastIndex) { 
                            $(tabClass + ":first-child").focus();
                        } else { 
                            $(tabClass).eq(tab.index() + 1).focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case _this.KeyCodes.home:
                        event.preventDefault();

                        // FOCUS THE FIRST TAB
                        $(tabClass + ":first-child").focus();
                    break;

                    // CONSUME END KEY
                    case _this.KeyCodes.end:
                        event.preventDefault();

                        // FOCUS THE LAST TAB
                        $(tabClass + ":last-child").focus();
                    break;

                    // CONSUME TAB KEY
                    case _this.KeyCodes.tab:
                        event.preventDefault();

                        // FOCUS THE LAST TAB
                        $(".gb-schools-menu.active li:first a").focus();
                    break;
                }
            }
        },

        "ChannelBar": function() {
            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });
        },

