link.script:
var navTop
$(document).ready(function () {
    var stickyElement = $("header");
    var stickyElement2 = $(".leftNav");
    navTop = stickyElement.offset().top
    var navTop2 = stickyElement2.offset().top
    if (navTop - 0 <= $(window).scrollTop()) {
        stickyElement.css("position", "fixed");
        stickyElement.css("top", "0");
        $(".wrapperElement").css("display", "block");
    } else {
        stickyElement.css("position", "static");
        $(".wrapperElement").css("display", "block");
    }
    if (navTop2 - 0 <= $(window).scrollTop()) {
        stickyElement2.css("position", "fixed");
        stickyElement2.css("top", "0");
    } else {
        stickyElement.css("position", "static");
    }
    $(window).scroll(function () {
        if (navTop - 0 <= $(window).scrollTop()) {
            stickyElement.css("position", "fixed");
            stickyElement.css("top", "0");
        } else {
            stickyElement.css("position", "static");
        }
        if (navTop - 0 <= $(window).scrollTop()) {
            stickyElement2.css("position", "fixed");
            stickyElement2.css("top", "0");
        } else {
            stickyElement2.css("position", "absolute");
            stickyElement2.css("top", navTop.toString());
        }
    });
});

