	]);

	$(function(){

    // SET IE CLASSES TO BODY
		if(isMSIE){ $("body").addClass("msie"); }
		if(isMSIE8){ $("body").addClass("msie8"); }
		if(isMSIE9){ $("body").addClass("msie9"); }
		if(isMSIE10){ $("body").addClass("msie10"); }

		$(window).bind("resize", function(){ jsMediaQueries(); csVideoResize(); headerHeight();stickyCalendarFix(); });

		head.ready("rsMainMenu", function(){
			var additionalHeaders = "", additionalLinks = "";

			// MENU GLOBAL ICONS
			var menuGlobalIcons = [
				["Calendar", "[SITECALENDARLINK]"],
				["Parent Portal", "https://campus.berea.k12.oh.us/campus/portal/berea.jsp"],
				["Staff Portal", "https://campus.berea.k12.oh.us/campus/berea.jsp"],
				["Final Forms", "https://www.berea.k12.oh.us/Page/3658"],
				["Webmail", "http://mail.bereaschools.org"],
                ["Webmail", "https://shop.equallevel.com/berea/login"]

			];
			var menuIconNum = ("6" == "6;5;4;3;2;1;0") ? 0 : "6";

			for(var i = 0; i < menuIconNum; i++) {
				var linkCheck = (menuGlobalIcons[i][1] == "[SITECALENDARLINK]") ? "/Page/2" : menuGlobalIcons[i][1];
				additionalLinks += "1, " + menuGlobalIcons[i][0] + ", " + linkCheck + ", _self;";
			}

			if(menuIconNum > 0) {
				additionalHeaders = "Popular Links";
			}

			// RESPONSIVE MAIN MENU
			$("body").csRsMenu({
			        "breakPoint"            : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			        "slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left   
			        "primaryColor"          : "#2C363E", // DEFAULT - #2C363E
			        "secondaryColor"        : "#222A30", // DEFAULT - #222A30
			        "accentColor"           : "#B5BBBF", // DEFAULT - #B5BBBF
			        "menuButtonParent"      : "#gb-channel-list",
			        "showMenuBtnText"       : "no",
			        "menuBtnText"           : "MENU",
			        "showSchools"           : "yes",
			        "showTranslate"         : "yes",
			        "translateId"           : "none",
			        "showAccount"           : "yes",
			        "extraMenuOptions"      : additionalHeaders, // EXTRA MENU HEADING NAMES SEPARATED BY A ;
			        "extraMenuItems"        : additionalLinks, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
			        "siteID"                : "1"
			});

		});

		head.ready("rsPagelistMenu", function(){
			// RESPONSIVE PAGELIST MENU
			$("body").csRsPagelistMenu({
			        "breakPoint"            : 480, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			        "slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left
			        "primaryColor"          : "#2C363E", // DEFAULT - #2C363E
			        "secondaryColor"        : "#222A30", // DEFAULT - #222A30
			        "accentColor"           : "#B5BBBF", // DEFAULT - #B5BBBF
			        "menuButtonParent"      : "#sp-content",
			        "showMenuBtnText"       : "yes",
			        "menuBtnText"           : "PAGES",
			        "siteID"                : "1"
			});
		});	

		head.ready("globalIcons", function() {
			var csIconNum = ("6" == "6;5;4;3;2;1;0") ? "0" : "6";
			$("#gb-global-icons-inner").creativeIcons({
			        "iconNum"      : csIconNum, // NUMBER OF ICONS
			        "iconColor"    : "#002e5f", // ICON COLOR
			        "icon1"        : ["Site Utility/1.png", "Calendar", "[SITECALENDARLINK]", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			        "icon2"        : ["/cms/lib6/OH01000054/Centricity/Template/GlobalAssets/icons/IClogo.png", "Parent Portal", "https://campus.berea.k12.oh.us/campus/portal/berea.jsp", "_blank"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
