	function jsMediaQueries(){
		switch(csGetBreakPoint()) {
			case "desktop": 
            	//vimeoPlayer();
				$(window).scroll(parallaxScroll);

			break;
			case "768":
            	//vimeoPlayer();
				appView();
			break;
			case "640":
            	//vimeoPlayer();
				appView();
			break;
			case "480":
            	//vimeoPlayer();
				appView();
			break;
			case "320":
            	//vimeoPlayer();
				appView();
			break;
		}
	}

