    function headerHeight(){

        var headerHeight = $("#gb-mystart-outer").height() + $("#gb-channel-list-outer").height();

        $("#sp-content-outer, #spn-content-outer").css("padding-top",headerHeight);
    }

	function mediaRotator() {
