    function stickyCalendarFix() {
    	var headerHeight = $("#gb-mystart-outer").height();
        var channelHeight = $("#gb-channel-list-outer").height();
        var calendarHeaderOffset = headerHeight + channelHeight;

        if ($(window).scrollTop() <= calendarHeaderOffset) {

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                $(".wcm-controls").css("margin-top", "0px");
            }
        } else {

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                if($(".wcm-controls").hasClass("wcm-stuck")) {
                    $(".wcm-controls").css("margin-top", calendarHeaderOffset + "px");
                }
            }
        }
    }

	function searchText() {
		//CUSTOM MYSTART SEARCH PLACEHOLDER
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "Search...");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search...") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search...");
			}
		});
	}

	function modEvents(){
		head.ready("modEvents", function() {
			if (!$(".upcomingevents .joel-month").length) {
				$(".upcomingevents").modEvents({
					columns     : "yes",
					monthLong   : "no",
					dayWeek     : "no"
				});
				eventsByDay(".upcomingevents .ui-articles");
			}
		});
	}		

	function eventsByDay(container) {
		//MOVE ALL EVENTS WITH THE SAME DATES INTO THE SAME CONTAINER
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();

			};
		});

        $("div.ui-widget.app.upcomingevents div.upcoming-column-container div.upcoming-column.left h1.ui-article-title").each(function(){
        	$(".joel-month", this).appendTo($(this));
        });

        //REMOVE ALL INSTANCES OF :00 in UPCOMING EVENT TIMES
        $("div.ui-widget.app.upcomingevents div.upcoming-column-container div.upcoming-column.right .ui-article-description").each(function(){
         	var time = $(".sw-calendar-block-time", this).text().replace(/\:00/g,"");
        	$(".sw-calendar-block-time", this).text(time);
        });
	}

	function calendarListView(){
		//AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}

    var parallaxScroll = function(){
		var getWidth = $(window).width();
		var getHeight = $(window).height();
        var mystartHeight = $("#gb-mystart-outer").height();
        var channelHeight = $("#gb-channel-list-outer").height();
        var headerHeight = mystartHeight + channelHeight;
		if (getWidth >= 1024) { // 960+
			if($(window).scrollTop() >= 1 && scrollLock == 0) {
            	$("#gb-sitename").hide();
            	$("#gb-header").addClass("sticky").css("min-height", headerHeight);
                $("#gb-header #gb-logo").hide();
                $("#gb-sitename").fadeIn(500);
                scrollLock = 1;

			} else if($(window).scrollTop() < 1 && scrollLock == 1) {
				$("#gb-header").removeClass("sticky").css("min-height","none");
                $("#gb-header #gb-logo").hide();
                $("#gb-sitename").fadeOut(500);
                $("#gb-header #gb-logo").fadeIn(500);
                scrollLock = 0;
			}

		}
	}

    function megaMenuDropdown() {
	var megaMenuData = [
		[
			["/cms/lib/OH01000054/Centricity/Template/GlobalAssets/images///Channel-Images/channel2.jpg", "WELCOME!", "The Berea City School District includes the cities of Berea, Brook Park, Middleburg Heights, and a portion of Olmsted Falls."]
		],
		[
			["/cms/lib/OH01000054/Centricity/Template/GlobalAssets/images///Channel-Images/channel6.jpg", "A Community of Excellence, Integrity, and Purpose", "The Berea City School District is a diverse community dedicated to inspiring lives of character, learning, leadership, and service."]
		],
		[
			["/cms/lib/OH01000054/Centricity/Template/GlobalAssets/images///Channel-Images/channel.jpg", "", ""]
