	// BUILD THE MEGA MENUS
    if($(window).width() >= 1024){
        $("#sw-channel-list-container li.sw-channel-item").each(function(){
            // LOWERCASE THE CHANNEL NAME
            var channelText = $("> a span", this).text().toLowerCase();

            // SET HOME AND CALENDAR CLASSES ON HOME AND CALENDAR CHANNELS
            if(channelText == "home") {
                $(this).addClass("home");
            } else if(channelText == "calendar") {
                $(this).addClass("calendar");
            }

            //IGNORE HOME AND CALENDAR CHANNELS
            if(channelText != "home" && channelText != "calendar") {
                // SET THE CHANNELS INDEX
                var i = ($("#sw-channel-list-container li.sw-channel-item.home").length) ? $(this).index() - 1 : $(this).index();

                // ADD MEGA CHANNEL CLASS
                $(this).addClass("mega-channel");

                // ADD STRUCTURE TO THE DOM
                $(".sw-channel-dropdown", this).removeAttr("class").wrap('

');

                // ADD IMAGE
                var imgColumn = '';
                if (megaMenuData[i] != undefined) {	/* Added by Lyndsay - case 00325853 */
                    var imgSrc = jQuery.trim(megaMenuData[i][0][0]);
                  //  console.log("image src: " + imgSrc);
                    var srcSplit = imgSrc.split("/");
                    var srcSplitLen = srcSplit.length;
                    if(imgSrc != "" && srcSplit[srcSplitLen - 1] != "default-man.jpg") {
                        imgColumn = '
';
                    }
                    $(".sw-channel-dropdown .mega-columns", this).prepend(imgColumn);

                    // ADD HEADER
                    var headerText = jQuery.trim(megaMenuData[i][0][1]);
                    if(headerText != ""){
                        var headerColumn = '
' + headerText + '';
                        $(".sw-channel-dropdown .mega-column.two", this).append(headerColumn);
                    }
                    // ADD CAPTION
                    var captionText = jQuery.trim(megaMenuData[i][0][2]);
                    if(captionText != ""){
                        var captionColumn = '
' + captionText + '';
                        $(".sw-channel-dropdown .mega-column.two", this).append(captionColumn);
                    }
				}
            }

            if($(".mega-column.one", this).length) {
                    $("ul.sw-channel-dropdown > li", this).css("width","665px");


                    //SET THE RIGHT POSITION OF THE DROPDOWN
                    var channelPos = $(this).position();
                    var channelPosLeft = channelPos.left;
                    var channelWidth = $("#sw-channel-list-container").width();
                    var channelItemWidth = $(this).width();
                    var channelDDOffset = -(channelWidth - channelPosLeft - channelItemWidth + 250);
                    $("ul.sw-channel-dropdown", this).css("right",channelDDOffset);
                } else {
                    $(".mega-column.two", this).css("width","180px");
                }

            if(!$(".mega-column.one", this).length && !$(".mega-column.two .mega-channel-header", this).length && !$(".mega-column.two .mega-channel-caption", this).length) {
                $(".mega-column.two ul", this).css("border","none");
                $(".mega-column.two ul", this).css("margin-bottom","0px");
                $(".mega-column.two ul", this).css("padding-bottom","0px");
            }
        });
    }    
}



	function csGetBreakPoint() {
		return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
	}

    function accessibility() {
    	var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

        $(".sw-mystart-dropdown").each(function(i) {
            // SET ATTRIBUTES
            $(".selector", this).attr("id", "mystart-dropdown-" + (i + 1));
            $(".sw-dropdown-list", this).attr("role", "menu").attr("aria-labelledby", $(".selector", this).attr("id"));
            $(".sw-dropdown", this).attr("aria-hidden", "true");
            $(".sw-dropdown-list li", this).attr("role", "menuitem");
            $(".sw-dropdown-list li a", this).attr("tabindex", "0");
        });

        /*$(".sw-mystart-dropdown").focus(function(e) {
            // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
            if(!$(this).hasClass("open")) {
                // TOGGLE THE DROPDOWN
