                $(".sw-dropdown-list li a", this).attr("tabindex","0");
            }
        });*/

        $("#sw-search-input").each(function() {
        		$(this).attr("tabindex","-1");
        });

        $("#sw-search-input").focus(function() {
        		$("#gb-search-inner").attr("aria-hidden","false");
        });


        $(".sw-mystart-dropdown").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME DOWN ARROW
                case csKeyCodes.enter:
                case csKeyCodes.space:
                    e.preventDefault();
                    $(".sw-dropdown", this).slideToggle("slow", "swing").attr("aria-hidden", ($(".sw-dropdown", this).attr("aria-hidden") == "true") ? "false" : "true");

                    // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                      if(!$(this).hasClass("open")) {
                          // TOGGLE THE DROPDOWN
                          $(this).toggleClass("open");
                      }
                      $(".sw-dropdown-list li a", this).attr("tabindex","0");
                    // FOCUS FIRST ITEM
                    $(".sw-dropdown-list li:first-child a", this).focus();
                break;
            }
        });

        $(".sw-mystart-dropdown .sw-dropdown-list li a").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS LAST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                break;
            }
        });

        $("#gb-global-icons-btn").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {

            	case csKeyCodes.space:
                case csKeyCodes.enter:
                	e.preventDefault();

                	$(this).next("#gb-global-icons-inner").slideToggle(500).attr("aria-hidden", ($("#gb-global-icons-inner").attr("aria-hidden") == "true") ? "false" : "true").find(".global-icon.first").focus();

                break;

            }
        });

        $("#gb-global-icons .global-icon").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {

                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                	e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).is(":first-child")) {
                        // FOCUS LAST ITEM
                        $(this).parent().find(".global-icon:last-child").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).prev(".global-icon").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                	e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if($(this).is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).parent().find(".global-icon:first-child").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).next(".global-icon").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).parent().find(".global-icon:first-child").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).parent().find(".global-icon:last-child").focus();
                break;
            }
        });

        $("#cs-sw-myaccount-list li").each(function(){
