// CHECK FOR IE AND SET IE VARIABLES
var isMSIE = 0, isMSIE8 = 0, isMSIE9 = 0, isMSIE10 = 0;
if(navigator.userAgent.indexOf("MSIE") > -1){ isMSIE = 1; }
if(navigator.userAgent.indexOf("MSIE 8") > -1){	isMSIE8 = 1; }
