link.script:
function translateMover() {
    if (window.innerWidth < 991)
        $(".GoogleDiv").appendTo(".slicknav_nav");
    else
        $(".GoogleDiv").appendTo(".HeaderRight");
}
$(document).ready(function () {
    translateMover();
});
$(window).resize(function () {
    translateMover();
});

