div.script:
function translateMover() {
    if (window.innerWidth < 991)
        $(".searchbox").appendTo(".slicknav_nav");
    else
        $(".searchbox").appendTo(".searchhome");
}
$(document).ready(function () {
    translateMover();
});
$(window).resize(function () {
    translateMover();
});

