$('.news_slider').slick({
    dots: true,
    infinite: true,
    arrows: false,
    slidesToShow: newsCount,
    slidesToScroll: 1,
    responsive: [
        {
            breakpoint: 992,
            settings: {
                slidesToShow: Math.ceil(newsCount / 2),
                slidesToScroll: 1,
                dots: false
            }
        },
        {
            breakpoint: 768,
            settings: {
                slidesToShow: 1,
                slidesToScroll: 1,
                dots: false
            }
        }
    ]
});
