div.script:
/* The Owl Initialization Script
/* The first lines conditionally show the slide animation */
jQuery(window).on("load", function(){
	var $galleryContainer = jQuery( '#foogallery-gallery-778' );
	var $containerID = '#foogallery-gallery-778';

	jQuery(document).on('keydown', function( event ) { //attach event listener
		if (event.keyCode == 37) {
			$galleryContainer.trigger('prev.owl')
		}
		if (event.keyCode == 39) {
			$galleryContainer.trigger('next.owl')
		}
	});

	
	$galleryContainer.owlCarousel({
	animateOut: 'rollOut',
	animateIn: 'rollIn',
		items: 1,
		nav: true,
		margin: 10,
		loop:true,
		autoplay: true,
		autoplaySpeed: 2000,
		smartSpeed:250,
		navSpeed: 1250,
		navText: ['prev', 'next'],
		autoplayHoverPause: true,
		lazyLoad: 1,
		autoWidth: 1,
			URLhashListener: false,
		startPosition: 'URLHash',
		dots: false,
			responsiveClass: true,
		responsive:{
			0:{
				items: 2,
			},
			480:{
				items: 3,
			},
			960:{
				items: 4,
			}
		},

			});
});
