$(document).ready(function() {

$('div.ui-widget.app').each(function(){
   if($.trim($(this).find('div.ui-widget-header > h1').html()) == ""){
      $(this).find('div.ui-widget-header > h1').css('border-bottom','0px none');
   }
});

//Hide empty headlines apps
$('div.ui-widget.app.headlines').each(function(){
	if($(this).find('div.ui-widget-detail > ul.ui-articles').length > 0){
   		if($.trim($(this).find('div.ui-widget-detail > ul.ui-articles').text()) == ""){
      		$(this).hide();
			$(this).find('div.ui-widget-footer').hide();
	  		$(this).find('div.ui-widget-header > h1').css('border-bottom','0px none');
   		}
	}
}); 
//Hide empty announcements apps
$('div.ui-widget.app.announcements').each(function(){
	if($(this).find('div.ui-widget-detail > ul.ui-articles').length > 0){
       if($.trim($(this).find('div.ui-widget-detail > ul.ui-articles').text()) == ""){
		  $(this).hide();
          $(this).find('div.ui-widget-footer').hide();
          $(this).find('div.ui-widget-header > h1').html('');
	      $(this).find('div.ui-widget-header > h1').css('border-bottom','0px none');
       }
}
});  



