        $.schoolListMinibase({
            'miniBaseDomainID': 4,
            'miniBaseModuleInstanceID': 6664,
            'cache': true,
            'schoolListReady': function(schoolList) {
                /*
                The returned value is an array of objects and each object is a school record.
                The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
                */

                var schools = '';

                for(var i in schoolList) {
                    if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                        schools += '
' + schoolList[i].schoolName + '';
                    }
                }

                $('.sw-mystart-dropdown.schoollist .sw-dropdown-list').html(schools);
                rsMenu(schoolList);
            }
        });
	});	

   function ibWorldLogoDynamicPositioning() {
   	if ($('#gb-channel-list').height() > 50) {
    	$('#ibWorldLogo.true').css('top', ($('#gb-channel-list').height() + 13));
    } else {
        $('#ibWorldLogo.true').css('top', '63px');
    }
   }
   function mystartDropDirection(){
		$(document).scroll(function() {
			var distanceFromTop = $(this).scrollTop();
			if($(".hp").length){
            	if(distanceFromTop >= 50){
                	$("#gb-mystart-outer").removeClass("on-bottom");
                } else {
                	$("#gb-mystart-outer").addClass("on-bottom");
                }
			}
		});
	}

   function accessibilityFunctions(){
		$("#sw-search-button").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
            	case csKeyCodes.tab:
                	if(e.shiftKey){

                    } else {
                        if($("#hp-player iframe").length){
                                        e.preventDefault();
                                        $(".anchor-one").focus();
                        }
                    }
    			break;

			}
		});

		$(".anchor-one").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                case csKeyCodes.tab:
                    if(e.shiftKey){
                                    e.preventDefault();
                                    $("#sw-search-button").focus();
                    } else {

                    }
                break;
                }
		});

	}

	function jsMediaQueries() {
		switch(csGetBreakPoint()) {
			case "desktop": 
				//$("#hp-content-top .hp.column.two").insertAfter("#hp-content-top .hp.column.one");
        		$("#hp-content-bottom .hp.column.two").insertAfter("#hp-content-bottom .hp.column.one");
			break;
			case "768":
				//$("#hp-content-top .hp.column.two").insertBefore("#hp-content-top .hp.column.one");
        		$("#hp-content-bottom .hp.column.two").insertBefore("#hp-content-bottom .hp.column.one");
			break;
			case "640":
            	//$("#hp-content-top .hp.column.two").insertBefore("#hp-content-top .hp.column.one");
        		$("#hp-content-bottom .hp.column.two").insertBefore("#hp-content-bottom .hp.column.one");
			break;
			case "480":
          		//$("#hp-content-top .hp.column.two").insertBefore("#hp-content-top .hp.column.one");
        		$("#hp-content-bottom .hp.column.two").insertBefore("#hp-content-bottom .hp.column.one");
			break;
			case "320":
				//$("#hp-content-top .hp.column.two").insertBefore("#hp-content-top .hp.column.one");
        		$("#hp-content-bottom .hp.column.two").insertBefore("#hp-content-bottom .hp.column.one");

                $("#global-icons-mobile-menu a").off('click').click(function(e) {
                	e.preventDefault();
                	$('div#gb-global-icons').toggleClass('open');
                });
			break;
		}
	}

     /*stickyHeader
		Use: It handles the sticky header that will make the header become fixed upon scrolling
		Parameter: shutoff -- this is a numeric value in which the width of the screen will stop the sticky header from activating.
	*/
	var csHeaderIsSticky = 0, csNavIsSticky = 0;
	function stickyHeader(shutoff, scroll){
		var getWidth = $(window).width();
		if ($('.hp').length) {
            if (getWidth >= 1024) {
                // HEADER
                var gbHeaderOffset = $("#gb-header-outer-ghost").offset().top;
                if ($(window).scrollTop() <= gbHeaderOffset) {
                    if(csHeaderIsSticky) {
                        $("#gb-header-outer").removeClass("sticky");
                        $("#gb-header-outer-ghost").css("height", "auto");
                        csHeaderIsSticky = 0;
                    }
                } else {
                    if(!csHeaderIsSticky) {
                        $("#gb-header-outer").addClass("sticky");
                        csHeaderIsSticky = 1;
                    }
                    $("#gb-header-outer-ghost").css("height", $("#gb-header-outer").outerHeight());
                }


                    // ANCHOR LINKS
                    var gbNavOffSet = $("#anchor-links-outer").offset().top - $("#gb-header-outer-ghost").height();
                    if ($(window).scrollTop() <= gbNavOffSet) {
                        if(csNavIsSticky) {
                            $("#anchor-links").removeClass("sticky");
                            $("#anchor-links-outer").css("height", "auto");
                            $("#anchor-links").css("top", "auto");

                            csNavIsSticky = 0;
                        }
                    } else {
                        $("#anchor-links-outer").css("height", $("#anchor-links").outerHeight());
                        $("#anchor-links").css("top", $(".hp #gb-header-outer-ghost").height());

                        if(!csNavIsSticky) {
                            $("#anchor-links").addClass("sticky");
                            csNavIsSticky = 1;
                        }
                    }
            } else {
                if(csHeaderIsSticky == 1) {
                    $("#gb-header-outer, #gb-header-outer-ghost").removeClass("on");
                    $("#gb-header-outer-ghost").css("height", "auto");

                    csHeaderIsSticky = 0;
                }

                if(csNavIsSticky == 1) {
                    $("#anchor-links").removeClass("sticky");
                    $("#anchor-links-outer").css("height", "auto");
                    $("#anchor-links").css("top", "auto");

                    csNavIsSticky = 0;
                }
            }
        } else {
        	$('#sp-content, #spn-content').css('padding-top', ($('header').height()));
        }

    }

     // GLOBAL ICONS
    function globalIcons() {
          $("#gb-global-icons").creativeIcons({
                    "iconNum"       : "4", // NUMBER OF ICONS
                    "icons"         : [
                            ["/cms/lib/GA01903590/Centricity/Template/GlobalAssets/images///icons/global-arts.png", "Performing Arts", "Center", "http://mpac.marietta-city.org/site/default.aspx?PageID=1697", "_blank"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
                            ["/cms/lib/GA01903590/Centricity/Template/GlobalAssets/images///icons/global-graduate.png", "Graduate", "Marietta","http://www.graduatemarietta.com/", "_blank"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
                            ["/cms/lib/GA01903590/Centricity/Template/GlobalAssets/images///icons/global-community.png", "Community", "School", "http://www.mariettacommunityschool.com/", "_blank"],  // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
                    		["/cms/lib/GA01903590/Centricity/Template/GlobalAssets/images///icons/global-teach.png", "Teach", "Marietta", "https://marietta.tedk12.com/hire/index.aspx", "_blank"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
                            ["/cms/lib/GA01903590/Centricity/Template/GlobalAssets/images///icons/global-teach.png", "Teach", "Marietta", "https://marietta.tedk12.com/hire/index.aspx", "_blank"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
                            ["/cms/lib/GA01903590/Centricity/Template/GlobalAssets/images///icons/global-reads.png", "Marietta", "Reads", "http://www.mariettareads.org/", "_blank"]  // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
                    ],
                    "siteID"        : "4",
                    "siteAlias"     : "mcs",
                    "calendarLink"  : "/Page/2",
                    "contactEmail"  : "webmaster@marietta-city.k12.ga.us",
                    "allLoaded"     : function(){ 
                    	$('#gb-global-icons .global-image').attr('alt', '');
                        $("#gb-global-icons .global-icon").each(function(k, v) {
                        	$(v).append('
');
                            $('.text.one', v).appendTo($('.two-line-link-title', v));
                            $('.text.two', v).appendTo($('.two-line-link-title', v));
                        });
                    }
            });
    }  

	function dynamicContent() {
		// ********** MYSTART ********** //
		$(document).off("click", ".sw-mystart-dropdown");
		$(document).on("click", ".sw-mystart-dropdown", function(e) {
			$(".sw-dropdown", this).slideDown(300, "swing");
			$(this).addClass("open");
		});
		$(".sw-mystart-dropdown").hover(function(e) {
			e.preventDefault();
		}, function() {
			$(".sw-dropdown",this).slideUp(300, "swing").attr('aria-hidden', 'true');
			$(this).removeClass("open");
		});
        $(document).off("focus", ".sw-mystart-dropdown");
		$(document).on("focus", ".sw-mystart-dropdown", function(e) {
			$(".sw-dropdown", this).slideDown(300, "swing").attr('aria-hidden', 'false');
			$(this).addClass("open");
		});
        $('Search: ').insertBefore('#sw-search-input');

		// ********** HEADER ********** //
		// ADD LOGO
		var logoCheck = "true";
		var logoSrc = jQuery.trim("/cms/lib/GA01903590/Centricity/Template/GlobalAssets/images///logos/marietta-logo.png");
		if(logoCheck == "true") {
			var srcSplit = logoSrc.split("/");
			var srcSplitLen = srcSplit.length;
			if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
				$("#gb-logo").append('').removeClass("hidden");
			} else {
				$("#gb-logo").append('').removeClass("hidden");
			}
		}

        //SEARCH BAR FUNCTIONALITY
        $("div#sw-mystart-search.sw-mystart-nav").unbind();
        $('#sw-search-button').attr('role', 'button');
        $('#sw-search-button').attr('aria-controls', 'sw-mystart-search');
        $('#sw-search-button').attr('aria-expanded', 'false');
        $('#sw-search-button').attr('aria-label', 'search');
        $('#sw-search-button').prop('onclick',null).off('click');
		 $('#sw-search-button').click(function(e){
        	if ($(window).width() >= 320 ) {
                $('#sw-mystart-search').stop(true, true).animate({width: 'toggle'}, 300);
                $(this).toggleClass('open');

                if( $('#sw-mystart-search').hasClass('showing-search') ){
                    $('#sw-mystart-search').removeClass('showing-search')
                    $('#sw-search-button').attr('aria-expanded', 'false');
                    $('#sw-mystart-search input[type="text"]').attr('tabindex', -1);
                }else{
                    $('#sw-mystart-search').addClass('showing-search')
                    $('#sw-search-button').attr('aria-expanded', 'true');
                    $('#sw-mystart-search input[type="text"]').attr('tabindex', 0);
                }
            }
        })
        .on('keypress', function(e) { // hooking into the default button trigger so we can stop it and run it only on keyup since we auto focus the search bar
            var code = e.which;
            // 13 = Return, 32 = Space
            if ((code === 13) || (code === 32)) {
                e.preventDefault();
            }
        })
        .on('keyup', function(e) { // Trigger the click event from the keyboard
            var code = e.which;
            // 13 = Return, 32 = Space
            if ((code === 13) || (code === 32)) {
                $(this).click();
            }
        });

		// ADD SITENAME
        var siteNameOne = jQuery.trim('');
		var siteNameTwo = jQuery.trim('');
		if((siteNameOne == "") && (siteNameTwo == "")) {
			var splitLen = 1;
			var siteName = "Marietta City Schools";
			siteName = siteName.split(" ");
			var siteNameLength = siteName.length;
			if(siteNameLength > 2){
				siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
			} else {
				siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
			}
			siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
			$("#gb-sitename").prepend("
" + siteNameBegin + "
" + siteNameEnd + "");
		} else if((siteNameOne != "") && (siteNameTwo == "")) {
			$("#gb-sitename").prepend("
" + siteNameOne + "");
		} else if((siteNameOne == "") && (siteNameTwo != "")) {
			$("#gb-sitename").prepend("
" + siteNameTwo + "");
		} else if((siteNameOne != "") && (siteNameTwo != "")) {
			$("#gb-sitename").prepend("
" + siteNameOne + "
" + siteNameTwo + "");
		}

        // MOVES MYSTART SEARCH
        if ($('.hp').length) {
            $("#sw-mystart-search").detach().appendTo('#gb-header-outer');
        } else {
        	$("#sw-mystart-search").detach().appendTo('#gb-mystart-outer');
        }
        //MOVES SEARCH BUTTON ABOVE SEARCH CONTAINER
            $('#sw-search-button').insertBefore('#sw-mystart-search');

		// ********** CHANNEL BAR ********** //
        if(($('.sw-channel-item.active').length > 0)){
			$('#gb-channel-list-bar').stop(false).animate({
				'width': $('.sw-channel-item.active').width(),
				'left': $('.sw-channel-item.active').position().left /* the 18 is an offset for the parents padding */
			}, 500);

			$(".sw-channel-item").unbind('hover');
			$(".sw-channel-item").hover(function(){ //in
				var subList = $(this).children('ul');
				if ($.trim(subList.html()) !== "") {
					subList.slideDown(200, "swing");
				}
				$(this).addClass("hover");

				if( !$(this).hasClass("school-list-channel") ){
					$('#gb-channel-list-bar').stop(false).animate({
						'width': $(this).width(),
						'left': $(this).position().left
					}, 500);
				}
			}, function(){ //out
				$(".sw-channel-dropdown").slideUp(200, "swing");
				$(this).removeClass("hover");
				if( !$('.sw-channel-item.active').hasClass('school-list-channel') ){
					$('#gb-channel-list-bar').stop(false).animate({
						'width': $('.sw-channel-item.active').width(),
						'left': $('.sw-channel-item.active').position().left
					}, 500);
				}
			});
		}else{
			$('#gb-channel-list-bar').hide();
		}

        //CHANGES 'CALENDAR' CHANNEL BAR ITEM TEXT TO A CALENDAR ICON
        ($('li.sw-channel-item:last-of-type > a span:contains("Calendar")')).parent().addClass('channel-calendar').attr('aria-label', 'Calendar');
		($('li.sw-channel-item:last-of-type > a span:contains("Calendar")')).text('');
        $(".channel-calendar").insertAfter("#gb-header-container");

		// ********** BODY ********** //
        $(".ff #player").css("display", "block"); //added by Lyndsay - 12.8.16 - Case 00439490 
		// AUTO FOCUS SIGN IN FIELD
		$("#swsignin-txt-username").focus();

		// CENTER SIGNED OUT MESSAGE AND SIGNIN BUTTON
        if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
            $("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
            //DOC add signed out breadcrumb
            $(".ui-breadcrumbs").append("
Signed Out");
        }

        // ADJUST SIGN IN PAGE
        if($("#swlogin").length){
            $("#sw-content-layout-wrapper .ui-widget.app").addClass("signin");
            $(".ui-breadcrumbs").append("
Sign In");
        }

        // ADJUST REGISTER PAGE
		if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
			$("#sw-content-layout-wrapper .ui-widget.app").addClass("register");
			$(".ui-breadcrumbs").append("
Register");
		}

		// ADJUST SEARCH RESULTS PAGE
		if($("#sw-content-layout-wrapper.ui-spn #swsearch-pnl-main").length) {
			$("#sw-content-layout-wrapper").children().wrapAll('

');
			$(".ui-breadcrumbs").append("
Search Results");
		}

        // ADD READ FULL STORY BUTTON TO EACH HEADLINE
        $(".ui-widget.app.headlines").each(function(){
            $(".ui-article", this).each(function(){
                var headlineURL = $("h1 a", this).attr("href");
                $(".ui-article-description", this).append('Read More');
