                smoothScroll($(this).attr('href'), $('#gb-header-outer.sticky').outerHeight());

                $($(this).attr('href')).children().first().attr('tabindex', 0).focus().attr('tabindex', '');
            });

            $('.anchor-one, .anchor-two, .anchor-three').on('keydown', function(e) { // Trigger the click event from the keyboard
                var code = e.which;
                // 13 = Return, 32 = Space
                if ((code === 13) || (code === 32)) {
                    e.preventDefault();

                    $(this).click();
                }
            });
        }
