		$(".gb-schoolwires-footer.links").append($("#sw-footer-links li:eq(2) a"));
		$(".gb-schoolwires-footer.links").append($("#sw-footer-links li:eq(1) a"));
		$(".gb-schoolwires-footer.links").append($("#sw-footer-copyright"));

         $('#gb-back-to-top').click(function(e){
        	smoothScroll('#gb-page', 0);
            $('#gb-logo > a').focus();
        });

        $('#gb-back-to-top').hide();
        $(window).scroll(function(e){
			if( $(window).scrollTop() >= ($(document).height() * 0.40) && !$('#gb-back-to-top').hasClass('showing')){
            	$('#gb-back-to-top').stop(true, true).fadeIn(800).addClass('showing');
            }else if( $(window).scrollTop() <= ($(document).height() * 0.40) && $('#gb-back-to-top').hasClass('showing')){
            	$('#gb-back-to-top').fadeOut(800).removeClass('showing');
            }
        });
        $("#gb-schoolwires-footer-right a").attr("aria-label", "Site Map");
	}

    function anchorLinkCheck() {
    	$('.main-hp-content-region').each(function(k, v){
         	var regClear = 0;
            $('.region', v).each(function(k1, v1){
            	if( $(v1).children().length <= 0 ) regClear++;
            });

            /*tried changing up the logic of this: prior it was just checking to see if any of the many regions was empty and then removing the button if 
            that was the case; flaw here is that having one of 4 regions empty shouldn't be negating the other regions with content. My change was to use a counter 
            and check against the number or regions rather than using a bool (0 or 1). Jeremy K - Case #04034066 */

            if( regClear == $('.region', v).length ){
            	$('#anchor-links a:eq('+ k +')').remove();
            }
        });
    }

