    function homepageCollage() {

	// LOOP THROUGH THE HEADLINES APP AND BUILD THE THUMBNAILS
	var collageThumbs = '';
	$("#hp-collage .region > div:first-child .ui-widget.app.headlines .ui-articles > li").each(function() {
		collageThumbs += '
';
	});

	// ADD THE THUMBS TO THE DOM
	$("#hp-collage").prepend('' + collageThumbs + '');

	// THUMBNAIL CLICK
	// WE'RE ONLY BUILDING THE SLIDER WHEN IT'S NECESSARY
	$(document).on("click keydown", ".hp-collage-thumb", function(e) {
		if(e.type == "click" || e.type == "keydown" && e.keyCode == csKeyCodes.space || e.type == "keydown" && e.keyCode == csKeyCodes.enter) {
			e.preventDefault();

			// UPDATE BUTTON ATTRIBUTES
			$("#hp-collage .hp-collage-thumb").attr("aria-pressed", "false");
			$(this).attr("aria-pressed", "true");

			// PASS THROUGH THE CLICKED THUMBAIL INSTANCE
			showCollageSlider($(this));
		}
	});
}

    function showCollageSlider(clickedThumb) {
        var headlinesApp = $("#hp-collage .region > div:first-child .ui-widget.app.headlines");

        // CHECK TO SEE IF THE SLIDER ALREADY EXISTS
        // IF IT DOESN'T, WE'LL HAVE TO BUILD IT
        if(!headlinesApp.hasClass("is-slider")) {
            // PREPARE ALL OF THE SLIDES
            $(".ui-articles > li", headlinesApp).each(function() {
              // APPLY THE IMAGES AS BACKGROUND IMAGES
              // THIS HELPS PRESERVE THE CORRECT SIZING AND RATIO'S IF THE WRONG SIZE IMAGE WAS UPLOADED
              $(".ui-article-thumb", this).css('background-image', 'url(' + $(".ui-article-thumb img", this).attr("src") + ')').attr('aria-label', $(".ui-article-thumb img", this).attr("alt") + ' - Click to read more');

              // WRAP EVERYTHING BUT THE IMAGE FOR EASIER CONTENT POSITIONING
              $(".ui-article", this).children().not($(".ui-article-thumb")).wrapAll('

');

              // IF MORE LINK EXISTS, CREATE AND ADD IT TO EACH SLIDE
              if($(".more-link", headlinesApp).length) {
                  $(".ui-article-slide-detail", this).append(' div:first-child .ui-widget.app.headlines .more-link").attr("href") + '">View All Headlines');
              }

              // ADD ATTRIBUTES
              $(this).attr("aria-hidden", "true");
              $("a", this).attr("tabindex", "-1");
          });

          // ADD SLIDER ID AND CREATE THE VIEWER WRAPPER
          $(".ui-articles", headlinesApp).attr("id", "headlines-slider").wrap('
');

          // ADD NAVIGATION ARROWS IF THEY ARE NEEDED
          if($(".ui-articles > li", headlinesApp).length > 1) {
              $(".ui-widget-detail", headlinesApp).prepend('').append('');
          }

          // APPLY THE is-slider CLASS SO WE KNOW IT'S BUILD
          headlinesApp.addClass("is-slider");

          // BIND THE SLIDER EVENTS
          collageSliderEvents(headlinesApp);
      }

        // QUEUE UP THE CORRECT SLIDE
        var slideWidth = $(".ui-articles > li:first-child", headlinesApp).width();
        var sliderPosition = (clickedThumb.index() * slideWidth) * -1;
        $(".ui-articles > li", headlinesApp).eq(clickedThumb.index()).addClass("active").attr("aria-hidden", "false").find("a").attr("tabindex", "0");
        $(".ui-articles", headlinesApp).css("left", sliderPosition);

        // FADE IN THE SLIDER
        $("#hp-collage .region").addClass("open").attr("aria-hidden", "false");

        // SET FOCUS TO THE SLIDE
        // DELAY THE ACTION TO GIVE THE SLIDE TIME TO FADE IN
        clearTimeout();
        setTimeout(function() {
            $(".ui-articles > li.active .ui-article-thumb", headlinesApp).focus();
        }, 400);
    }

    function collageSliderEvents(headlinesApp) {
        // CLOSE THE OVERLAY CLICK
        $(document).on("click", "#hp-collage .region", function() {
            $("#hp-collage .region").removeClass("open").attr("aria-hidden", "true");

            // SET FOCUS AND UPDATE BUTTON ATTRIBUTES
            $("#hp-collage .hp-collage-thumb").attr("aria-pressed", "false");
            $("#hp-collage .hp-collage-thumb:first-child").focus();

            // REMOVE THE SLIDER POSITION TO YOU DON'T SEE IT SCROLLING TO CHOSEN SLIDE WHEN OVERLAY IS OPENED AGAIN
            // DELAY IT SO THE OVERLAY HAS TIME TO FADE OUT AND YOU DON'T SEE IT JUMP
            clearTimeout();
            setTimeout(function() {
                $(".ui-articles", headlinesApp).removeAttr("style");
                $(".ui-articles > li", headlinesApp).removeClass("active").attr("aria-hidden", "true");
                $(".ui-articles > li a", headlinesApp).attr("tabindex", "-1");
            }, 400);
        });
        // STOP THE CLOSE CLICK PROPAGATION
        $(document).on("click", "#hp-collage .region > div", function(e) {
            e.stopImmediatePropagation();
        });

        // PREVIOUS AND NEXT SLIDER BUTTONS
        $(document).on("click keydown", ".headlines-slider-button", function(e) {
            if(e.type == "click" || e.type == "keydown" && e.keyCode == csKeyCodes.space || e.type == "keydown" && e.keyCode == csKeyCodes.enter) {
                e.preventDefault();
                var slideWidth = $(".ui-articles > li:first-child", headlinesApp).width();
                var sliderPosition = "";
                var activeSlide = "";

                // PREVIOUS BUTTON
                if($(this).hasClass("prev")) {
                    // IF FIRST ARTICLE IS ACTIVE, NAVIGATE TO LAST SLIDE
                    if($(".ui-articles > li.active", headlinesApp).is(":first-of-type")) {
                        sliderPosition = (($(".ui-articles > li", headlinesApp).length - 1) * slideWidth) * -1;
                        activeSlide = $(".ui-articles > li:last-child", headlinesApp).index();
                    } else {
                        sliderPosition = ($(".ui-articles > li.active", headlinesApp).prev("li").index() * slideWidth) * -1;
                        activeSlide = $(".ui-articles > li.active", headlinesApp).prev("li").index();
                    }
                }

                // NEXT BUTTON
                else {
                    // IF LAST ARTICLE IS ACTIVE, NAVIGATE TO FIRST SLIDE
                    if($(".ui-articles > li.active", headlinesApp).is(":last-of-type")) {
                        sliderPosition = 0;
                        activeSlide = 0;
                    } else {
                        sliderPosition = ($(".ui-articles > li.active", headlinesApp).next("li").index() * slideWidth) * -1;
                        activeSlide = $(".ui-articles > li.active", headlinesApp).next("li").index();
                    }
                }

                // SET THE ACTIVE CLASS
                $(".ui-articles > li", headlinesApp).removeClass("active").attr("aria-hidden", "true");
                $(".ui-articles > li a", headlinesApp).attr("tabindex", "-1");
                $(".ui-articles > li", headlinesApp).eq(activeSlide).addClass("active").attr("aria-hidden", "false").find("a").attr("tabindex", "0");

                // SLIDE THE PANEL
                $(".ui-articles", headlinesApp).css("left", sliderPosition);
            }
        });
    }

    function hpBgVideo() {
    var videoID = $.trim("395542709");
    if(videoID == "") {
    	videoID = "176757783";
    }

	if(($("#player").length) && $('#hp-main-img-or-video-outer').hasClass('true') ) {
		// VIMEO API - https://developer.vimeo.com/player/js-api | https://player.vimeo.com/playground
		// CUSTOMIZE EMBED - https://vimeo.com/blog/post/customize-the-embedded-vimeo-player
        var options = {
            id: videoID,
            width: '100%',
            loop: true
        };

        $('#player').hide();

        var player = new Vimeo.Player('player', options);
		var videoStarted = false;
		csVideoResize();
		// VIMEO PLAYER IS READY
		player.ready().then(function() {
        	csVideoResize();
          $('#player').show();
          player.play();
              // MUTE THE VIDEO
          player.setVolume(0).then(function(volume) {
              // volume was set
          }).catch(function(error) {
        	console.log(error);
            switch (error.name) {
                case 'RangeError':
                    // the volume was less than 0 or greater than 1
                    break;

                default:
                    // some other error occurred
                    break;
            }
       	   });

			// LOOP THE VIDEO
			if(!$(".ie9").length) {
				player.setLoop(true);
			}

			// BIND VIDEO EVENTS
			$(".play-pause").click(function() {
				// PAUSE
				if($(this).hasClass("pause")) {
					player.pause();
					$(this).removeClass("pause").addClass("play");
				}

				// PLAY
				else {
					player.play();
					$(this).removeClass("play").addClass("pause");
				}

			});
		});
	} else {
    	$('#hp-main-img-or-video-outer #player').remove();
    }
}

function csVideoResize() {
	if(($(".hp").length)) {
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+ | FULL SCREEN DIMENSIONS
			var topOffSet = $("#hp-video").offset().top;
			var screenWidth = $(window).width();
			var screenHeight = $(window).height() - 50;
			var screenRatio = screenHeight / screenWidth;
				var containerHeight = screenWidth * screenRatio;
			var vidWidth = screenWidth, vidHeight = 0;

			// GET IMAGE RATIO
			var initW = 1500, initH = 915;
			var ratio = initH / initW;
			vidHeight = vidWidth * ratio;
			if(vidHeight < screenHeight){
				vidHeight = screenHeight;
				vidWidth = vidHeight / ratio;
			}
			var centerWidth = (vidWidth - screenWidth) / 2;
			var centerHeight = (vidHeight - screenHeight) / 2;


			// APPLY THE DIMENSIONS
			$("#hp-video, #hp-main-bg-img").css({"height": containerHeight + "px"});

            $("#player iframe, #hp-main-bg-img img").css({"width": vidWidth + "px", "height": vidHeight + "px", "margin": "-" + centerHeight + "px 0px 0px -" + centerWidth + "px"});

            if ($('#hp-main-bg-img > div').height() < $(window).height()) { 
            	$('#hp-main-bg-img > div').height($(window).height());
                $('#hp-main-bg-img > div').css('margin', '0');
            }
        } if (getWidth < 1024) { // 768- | ROTATOR DIMENSIONS
			// GET IMAGE RATIO
			var initW = 853.5, initH = 480;
			var ratio = initH / initW;
			var vidWidth = $(window).width();
			vidHeight = vidWidth * ratio;

			// APPLY THE DIMENSIONS
			$("#hp-video, #hp-main-bg-img").css({"height": vidHeight + "px"});
			$("#player iframe, #hp-main-bg-img img").css({"width": "100%", "height": vidHeight + "px", "margin": "0px"});
		} 
	}
}

