html.script:
		// $(document).ready(function ()
		// {
		// 	initializeForm();
		// });

		function initializeForm()
		{
			$('#gettingStartedFormPanel').show();
			$('#gettingStartedThankYouPanel').hide();

			$('#ccems-error-panel').html('');

			$('#FullName').val('');
			$('#Email').val('');
			$('#Phone').val('');
			$('#District').val('');
			$('#gettingStartedCMS').attr('checked', false);
			$('#Communication').attr('checked', false);
			$('#EMS').attr('checked', false);
			$('#App').attr('checked', false);
			$('#Message').val('');
			$('#Quote').attr('checked', false);
			$('#Demo').attr('checked', false);

			$('#ccems-submitButton').show();
			$('#ccems-submittingButton').hide();

			$('#pdfc-security-captcha-password').val('');
			$('#pdfc-security-captcha-response').val('');
			$('#pdfc-security-verify').attr('disabled', false);
			$('#pdfc-security-fancyspan').show();
			$('#pdfc-security-verified').hide();
			$('#pdfc-security-loading').hide();

			$("#ccems-submitButton").click(ccemsSubmitButton_click);
			$('#pdfc-security-verify').on('click', verifyCheckbox_click);
		}

		function verifyCheckbox_click(e)
		{
			$('#pdfc-security-captcha-password').val(pdfcGetRandNumber());
			$('#pdfc-security-captcha-response').val('');

			try
			{
				$('#pdfc-security-fancyspan').hide();
				$('#pdfc-security-loading').show();

				pdfc.global.connect('PUT', { 'Password': $('#pdfc-security-captcha-password').val() }, "Connector/NewEmailPassword", function(response)
				{
					$('#pdfc-security-captcha-response').val(response);
					$("#pdfc-security-verify").attr('disabled', true);
					$("#pdfc-security-loading").hide();
					$("#pdfc-security-verified").show();
				}, function(jqXHR, textStatus, errorThrown)
				{
					$("#pdfc-security-verify").removeAttr('checked');
					$("#pdfc-security-fancyspan").show();
					$("#pdfc-security-loading").hide();
					console.log("verifyCheckbox_click(): " + textStatus + " : " + errorThrown);
				});
			}
			catch (ex) { console.log(ex); }
		}

		function ccemsSubmitButton_click(e)
		{
			e.preventDefault();

			$('#ccems-error-panel').html('');

			$('#ccems-submitButton').hide();
			$('#ccems-submittingButton').show();

			try
			{
				var item = {};
				item.FullName = $('#FullName').val();
				item.EmailAddress = $('#Email').val();
				item.PhoneNumber = $('#Phone').val();
				item.SchoolDistrict = $('#District').val();

				var interestedIn = "";

				if($('#gettingStartedCMS').is(':checked')) { interestedIn += (interestedIn == "" ? "" : ", ") + $('#gettingStartedCMS').val(); }
				if($('#Communication').is(':checked')) { interestedIn += (interestedIn == "" ? "" : ", ") + $('#Communication').val(); }
				if($('#EMS').is(':checked')) { interestedIn += (interestedIn == "" ? "" : ", ") + $('#EMS').val(); }
				if($('#App').is(':checked')) { interestedIn += (interestedIn == "" ? "" : ", ") + $('#App').val(); }

				item.InterestedIn = interestedIn;
				item.ProvideQuote = ($('#Quote').is(':checked') ? "Yes" : "No");
				item.WantsDemo = ($('#Demo').is(':checked') ? "Yes" : "No");

				item.Message = $('#Message').val();

				item.Password = $('#pdfc-security-captcha-password').val();
				item.EmailAuthId = $('#pdfc-security-captcha-response').val();

				// check for valid input
				var errorMessage = "";
				if(item.FullName == '') { errorMessage += "Please enter your full name.
"; }

				if (item.EmailAddress == "") { errorMessage += "Please enter your Email.
"; }
				else if (!/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(item.EmailAddress)) { errorMessage += "Please enter a valid Email.
"; }

				if(item.PerferedDateTime == '') { errorMessage += "Please enter when you would like to be contacted.
"; }

				if(item.EmailAuthId == '') { errorMessage += "Please select that you are not a robot
"; }

				if (errorMessage != "")
				{
					$('#ccems-error-panel').html(errorMessage);
					$('#ccems-error-panel').focus();

					$('#ccems-submitButton').show();
					$('#ccems-submittingButton').hide();
					return;
				}

				pdfc.global.connect('PUT', item, "Connector/GettingStartedV2", function(response)
				{
					$('#ccems-submitButton').show();
					$('#ccems-submittingButton').hide();

					$('#gettingStartedFormPanel').hide();
					$('#gettingStartedThankYouPanel').show();

				}, function (jqXHR, textStatus, errorThrown)
				{
					$('#ccems-error-panel').val('Failed to submit the contact form.  If the problem continues please contact a system administrator.');
					$('#ccems-submitButton').show();
					$('#ccems-submittingButton').hide();
				});
			}
			catch(ex)
			{
			}
		}
