$('a').each(function () {
    var a = new RegExp('/' + window.location.host + '/');
    var link = this.href;
    if (!link.includes('javascript:'){
        if (!link.includes('www.swl')) {
            if (!a.test(this.href)) {
                $(this).click(function (event) {
                    event.preventDefault();
                    event.stopPropagation();
                    window.open('http://www.swl.k12.oh.us/protected/ExternalDomainLink.aspx?lnk=' + link);
                });
            }
        }
    }
});
