function init_map(){
    var myStyles = [{"featureType":"road","elementType":"geometry","stylers":[{"visibility":"simplified"},{"color":"#f7f7f7"},{"weight":0.37}]},{"featureType":"landscape","elementType":"geometry","stylers":[{"saturation":-54},{"lightness":12}]},{"featureType":"poi","elementType":"all","stylers":[{"hue":"#74bbd4"},{"saturation":-69},{"lightness":29}]},{"featureType":"water","elementType":"geometry","stylers":[{"hue":"#1cdbd1"},{"saturation":-56},{"lightness":-25},{"gamma":1.09}]}];




    var myOptions = {
      zoom:12,scrollwheel:false, center:new google.maps.LatLng(41.528906,-81.62573700000002), styles: myStyles
    };

    map = new google.maps.Map(document.getElementById("gmap_canvas"), myOptions, myStyles);

    marker = new google.maps.Marker({map: map,position: new google.maps.LatLng(41.528906, -81.62573700000002)});

    infowindow = new google.maps.InfoWindow({content:"Stonebrook Montessori
975 East Boulevard
44108 Cleveland" });


    google.maps.event.addListener(marker, "click", function(){infowindow.open(map,marker);});infowindow.open(map,marker);
  }



    google.maps.event.addDomListener(window, 'load', init_map);
