    var topNavColors = ["#FF69B4", "#DE0A0F", "#800080", "#1E7ACA", "#0000FF", "#002366", "#008000", "#228b22"];
    function slidingColors(_c, _element){
        $(window).scroll(function(){
            var full = $(document).outerHeight() - window.innerHeight;
            var current = window.scrollY;
            var percent = current / full;
            percent *= (_c.length - 1);
            var lowerC = _c[Math.floor(percent)];
            var upperC = _c[Math.ceil(percent)];
            var truePercent = percent - Math.floor(percent);
            lowerC = lowerC.replace("#", "");
            upperC = upperC.replace("#", "");
            lcParts = lowerC.split("");
            ucParts = upperC.split("");
            for(var l in lcParts){
                if (isNaN(parseInt(lcParts[l]))){
                    switch(lcParts[l].toUpperCase()){
                        case "A":
                            lcParts[l] = 10;
                            break;
                        case "B":
                            lcParts[l] = 11;
                            break;
                        case "C":
                            lcParts[l] = 12;
                            break;
                        case "D":
                            lcParts[l] = 13;
                            break;
                        case "E":
                            lcParts[l] = 14;
                            break;
                        case "F":
                            lcParts[l] = 15;
                            break;
                        default:
                            lcParts[l] = 0;
                            break;
                    }
                }else{
                    lcParts[l] = parseInt(lcParts[l]);
                }
            }
            for(var u in ucParts){
                if (isNaN(parseInt(ucParts[u]))){
                    switch(ucParts[u].toUpperCase()){
                        case "A":
                            ucParts[u] = 10;
                            break;
                        case "B":
                            ucParts[u] = 11;
                            break;
                        case "C":
                            ucParts[u] = 12;
                            break;
                        case "D":
                            ucParts[u] = 13;
                            break;
                        case "E":
                            ucParts[u] = 14;
                            break;
                        case "F":
                            ucParts[u] = 15;
                            break;
                        default:
                            ucParts[u] = 0;
                            break;
                    }
                }else{
                    ucParts[u] = parseInt(ucParts[u]);
                }
            }

            var newColor = "rgb(";
            var temp = 0;
            temp = ((((ucParts[0] * 16) + ucParts[1]) - ((lcParts[0] * 16) + lcParts[1])) * truePercent) + ((lcParts[0] * 16) + lcParts[1]);
            newColor += (temp).toString() + ", ";
            temp = ((((ucParts[2] * 16) + ucParts[3]) - ((lcParts[2] * 16) + lcParts[3])) * truePercent) + ((lcParts[2] * 16) + lcParts[3]);
            newColor += (temp).toString() + ", ";
            temp = ((((ucParts[4] * 16) + ucParts[5]) - ((lcParts[4] * 16) + lcParts[5])) * truePercent) + ((lcParts[4] * 16) + lcParts[5]);
            newColor += (temp).toString() + ")";
            $(_element).css("background", newColor);
        });
        $(window).scroll();
    }
    $(document).konami({
        result: function(){
            $(".TopNav nav").css({"position": "fixed", "border-top": "1px solid #001"});
            $(".subfooter").css("margin-bottom", "40px")
            slidingColors(topNavColors, $(".TopNav nav")[0]);
        }
    });

