        var width = $('#' + calId).width();
        var calYearId = 'miniCalYear20200429100942138';
        var calMonthFullId = 'miniCalMonthFull20200429100942138';
        var calMonthShortId = 'miniCalMonthShort20200429100942138';
        var calGoToCalFullId = 'miniCalGotToCalFull20200429100942138';
        var calGoToCalShortId = 'miniCalGoToCalShort20200429100942138';
        if(width < 250) {
            $('#' + calYearId).hide();
            $('#' + calGoToCalFullId).hide();
            $('#' + calGoToCalShortId).show();
        }
        else {
            $('#' + calYearId).show();
            $('#' + calGoToCalFullId).show();
            $('#' + calGoToCalShortId).hide();
        }
        if(width < 170) {
                $('#' + calMonthFullId).hide();
                $('#' + calMonthShortId).show();                                       
        }
        else {
                $('#' + calMonthFullId).show();
                $('#' + calMonthShortId).hide();                                       
        }
    }               
}

