    function scrollToItem(item) {
        var elem = $(item);
        var scrollTo = elem.offset().top;

        if ($(window).outerWidth() > 991.9) {
            scrollTo -= 100;
        }

        $('html, body').animate({
            scrollTop: scrollTo
        }, 600, "swing");
    }

