;(function($) { 'use strict';
    var ids = '';


  if (ids) {
    ids = ids.replace(/,$/g, '');
    $(ids).css('display', 'none');
  }

  function clearMenus() {
    // Remove the "open" class from all dropdowns and reset all aria attributes
    $('#mainNav .open, #subNav .open').removeClass('open');
    $('#mainNav [aria-expanded], #subNav [aria-expanded]').attr('aria-expanded', 'false');
    $('#mainNav [aria-hidden], #subNav [aria-hidden]').attr('aria-hidden', 'true');
    $('#mainNav .main-nav-body').css({'overflow': '', 'height': ''});
  }

  // Remove duplicated attributes
  $('#mainNav .main-nav-body > .dropdown-menu').removeAttr('aria-expanded').removeAttr('aria-hidden');

  $(function() {

    // If the buckets have zero height, hide them so tabbing will bypass them unless opened.
    $('#mainNav .main-nav-body').each(function() {
      if ($(this).height() == 0) $(this).addClass('hidden');
    });

    // Keyboard activity ------------------------------------------------------

    $('#mainNav, #subNav').on('keydown', '.dropdown > a, .dropdown-submenu > a, .hasSubs > a', function(e) {

      // Watch for the ENTER key
      var keyCode = e.keyCode || e.which;
      if (keyCode == 13) {
        var $liParent = $(this).parent();

        // Toggle "open" class on "dropdown" elements
        if ($liParent.hasClass('open')) {
          $liParent.removeClass('open');

          // Change aria attributes
          $(this).attr('aria-expanded', 'false').next().attr('aria-expanded', 'false').attr('aria-hidden', 'true');

          // If the parent  is the first  in the bucket - remove the inline overflow:visible rule added below.
          if ($liParent.parent().parent().hasClass('main-nav-body')) $(this).closest('.main-nav-body').css({'overflow': '', 'height': ''});
        } else {
          $liParent.addClass('open');

          // Change aria attributes
          $(this).attr('aria-expanded', 'true').next().attr('aria-expanded', 'true').attr('aria-hidden', 'false');

          // If the parent  is the first  in the bucket - override the overflow:hidden rule so that we can "see" the flyout bucket.
          if ($liParent.parent().parent().hasClass('main-nav-body')) $(this).closest('.main-nav-body').css({'overflow': 'visible', 'height': 'auto'});
        }

        // Prevent browser from following link - this link will act as a toggle for displaying/hiding the dropdown
        e.preventDefault();
      }

    });

    // Close any previously opened dropdown menus as the user tabs to a new 1st-level navbar item
    $('#mainNav, #subNav').on('focus', '> li > a', function(e) {
      if ( ! $(this).parent().hasClass('open')) clearMenus();
    });

    // Close any previous opened flyout menu as the user tabs to a new 2nd-level navbar item
    $('#mainNav').on('focus', '.dropdown-menu > li > a', function(e) {
      if ( ! $(this).parent().hasClass('open')) {
        $(this)
          .closest('.dropdown-menu').find('.open').removeClass('open')
          .children('a').attr('aria-expanded', 'false').next().attr('aria-expanded', 'false').attr('aria-hidden', 'true');

        // If the parent  is the first  in the bucket - remove the inline overflow:visible rule
        if ($(this).closest('ul').parent().hasClass('main-nav-body')) $(this).closest('.main-nav-body').css({'overflow': '', 'height': ''});
      }
    });

    // Close any previously opened dropdown menus when the focus leaves the navbar
    $('#mainNav, #subNav').on('blur', 'li:last > a', function(e) {
      if ($(e.relatedTarget).closest('#mainNav, #subNav').length === 0) clearMenus();
    });

    $('html').on('click keydown', function(e) {
      if (e.type === 'click') {
        // Close the menu when clicking "outside" the menu...
        if ($(e.target).closest('.open').length === 0) clearMenus();
      } else {
        // Close any open menus if the ESCAPE key is pressed
        var keyCode = e.keyCode || e.which;
        if (keyCode == 27) clearMenus();
      }
    });

    // Hover activity ---------------------------------------------------------

    // Close any keyboard "opened" menus when the mouse enters the navbar
    $('#mainNav, #subNav').on('mouseenter', function(e) {
      if ($(this).find('.open').length) clearMenus();
    });

    // Change aria attribute values when mouse enters/leaves dropdown targets
    $('#mainNav').on('mouseenter', '.dropdown, .dropdown-submenu', function(e) {
      $(this).children('a').attr('aria-expanded', 'true').next().attr('aria-expanded', 'true').attr('aria-hidden', 'false');
    });
    $('#mainNav').on('mouseleave', '.dropdown, .dropdown-submenu', function(e) {
      $(this).children('a').attr('aria-expanded', 'false').next().attr('aria-expanded', 'false').attr('aria-hidden', 'true');
    });
    $('#subNav').on('mouseenter', '.hasSubs', function(e) {
      $(this).children('a').attr('aria-expanded', 'true').next().attr('aria-expanded', 'true').attr('aria-hidden', 'false');
    });
    $('#subNav').on('mouseleave', '.hasSubs', function(e) {
      $(this).children('a').attr('aria-expanded', 'false').next().attr('aria-expanded', 'false').attr('aria-hidden', 'true');
    });

  });
})(jQuery);

;(function($){
  'use strict';

  // Hide the tabs that don't have any content
  if ($('#tb_twitter div').length == 0) $('#sideTabs ul.tabsNav a[href$="twitter"]').parent().addClass('hidden');
  if ($('#tb_facebook div').length == 0) $('#sideTabs ul.tabsNav a[href$="facebook"]').parent().addClass('hidden');

  // If all tabs are hidden - no content - hide the ul, too
  if ($('#sideTabs ul.tabsNav li:visible').length == 0) $('#sideTabs ul.tabsNav').css('display', 'none');

  $(function() {
    var $links = $('#sideTabs .tabsNav a:visible'),
      panelIds = $links.map(function() { return this.hash; }).get().join(","),
      $panels = $(panelIds),
      $panelWrapper = $panels.first().parent(),
      tabDelay = 500,
      tabDisableEvent = false;

    $panels.css('display', 'none').first().fadeIn();
    $links.first().addClass('selected');

    $links.click(function() {
      var link = this, $link = $(this);

      // Ignore if already visible
      if ($link.is('.selected')) return false;

      // Don't want multiple clicks on different "tabs" to screw things up - process one click at a time
      if (tabDisableEvent) return false;

      tabDisableEvent = true;

      // Clear any height that may have been set by the CH Slider and stop the timer
      $panelWrapper.css('height', '');

      // Get the ID of the panel matching this ling
      var panelID = $links.filter('.selected').attr('href');

      $links.removeClass('selected');
      $link.addClass('selected');

      $(panelID).children('div').stop().fadeOut(tabDelay);

      $(panelID).stop().fadeOut(tabDelay, function() {
        $(this).children('div').show();
        $(link.hash).fadeIn(tabDelay, function() {
          tabDisableEvent = false;
        });
      });

      return false;
    });

  });
})(jQuery);

(function($){
  $(function() {

    var divH = 0, maxHeight = 0;

    $('#calendarWeek td div:has(ul)').each(function() {
      var $div = $(this);
      var ulH = $div.find('ul').height();
      divH = $div.height();

      // Get the tallest  in the group
      if (ulH > maxHeight) maxHeight = ulH;

      // If the  is taller than the 
...
      if (ulH > divH) {
        // Which  is this 
 in?
        var tdCount = $div.parent().prevAll().length + 1;

        // Find the same number  in the "more" row
        var $tdMore = $('#calendarWeek tr.more td:nth-child(' + tdCount + ')');

        // Add a "More..." link
        $tdMore.append('More...<\/a>');
      }
    });

    var $uls = $('#calendarWeek td div ul');
    var $links = $('#calendarWeek tr.more td a');
    $links.click(function() {
      var $this = $(this);
      if ($this.text() === 'More...') {
        $uls.each(function() {
          // Animate a "slide down" effect on the parent div's height
          $(this).parent().animate({'height': maxHeight});
        });
        $links.text('...Less');
      } else {
        $uls.each(function() {
          // Animate a "slide up" effect to the parent div's original height
          $(this).parent().animate({'height': divH});
        });
        $links.text('More...');
      }
      $this.blur(); // Lose focus to get rid of the outline
      return false;
    });

  });
})(jQuery);

