        csWindowHeight = $(window).height(),
        centerPoint = csWindowWidth/2,
        channelBarHeight = $("#gb-channel-list-outer").height(),
        gbHeaderHeight = $("#gb-header-outer").height(),
        scrollPos = $(window).scrollTop(),
        lastScrollTop = $(document).scrollTop(),
        detectScroll = 5,
