	function logoWidth() {
		if($("#gb-logo img").length) {
			var imgWidth = $("#gb-logo img").width();
			$("#gb-logo").width(imgWidth);
		}
	}

	function logoWidth480() {
    	var getLogoWidth = $("#gb-logo img").width();
		if($("#gb-logo img").length) {
			var imgWidth = getLogoWidth*.75;
			$("#gb-logo, #gb-logo img").width(imgWidth);
		}
	}

	function appView(){
    	if(!$("#hp-content-top div.ui-widget.app.active").length) {
        	$("#hp-content-top div.ui-widget.app").first().addClass("active");
        }

		//DOC dont add app into accordion if it doesnt have a header
		$("#hp-content-top div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});

		//DOC accordion header click
        $('#hp-content-top .ui-widget-header')
        	.off('click') //these needs to be here to remove click events when mobile devices scroll (which causes a screen resize event)
            .click(function(){
              if( $(this).parent().hasClass("active") ){ //If they click the same header again it will hide
                  $(this).parent().removeClass("active");
              }else{
                  var clickedApp = $(this).parent().parent().parent().attr("id");
                  $(this).parent().addClass("active");
                  smoothScroll("#" + clickedApp, 3);
              }
       		});
	}	

    function smoothScroll( to, offset ){
        if( to.indexOf("#") < 0 ){ to = "#" + to; } //makes sure we have a valid id accessor string
        var target = $(to);

        if (target.length) {
            $('html,body').animate({
                scrollTop: target.offset().top - offset //this offset accounts for the fixed menu on the top
            }, 800, function(){
                $("html, body").stop(true, false);
            });
            return false;
        }
    }

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
    }

	function dynamicContent(){
		// ********** MYSTART ********** //
        //DOC move mystart left buttons to mystart right
        var mystartLeft = $("#sw-mystart-left").html();
        $(mystartLeft).prependTo("#sw-mystart-right");
        $("#sw-mystart-left").remove();
        //DOC move mystart into header
        $("#sw-mystart-outer").prependTo("#gb-header").show();
		//DOC fixes search box not allowing spaces
		$("#sw-mystart-search").unbind();
		//DOC replace mystart dropdown arrows
		$(".sw-dropdown-arrow").addClass("insight-icon-font").html("");


        $("#sw-mystart-account #sw-myaccount").html('User Options ');
        $("#sw-mystart-mypasskey > a span").text("Passkeys");
        $(".sw-mystart-dropdown.schoollist .selector").text("Select a School");

        //DOC move passkey button before user options dropdown
        if($("#sw-mystart-mypasskey").length) {
        	$("#sw-mystart-account").before($("#sw-mystart-mypasskey"));
        }

		//UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
		$(".sw-mystart-dropdown.schoollist .sw-dropdown").attr("id","cs-schoollist-dropdown-list");
		$(".sw-mystart-dropdown.schoollist").off();
		$(".sw-mystart-dropdown.schoollist").click(function(){
			$(".sw-dropdown-arrow",this).addClass("rotate-arrow");
			$("#cs-schoollist-dropdown-list").slideDown(300, "swing");
		});

		$(".sw-mystart-dropdown.schoollist").hover(function(){

		}, function(e) {
			e.stopPropagation();
			$(".sw-dropdown-arrow").removeClass("rotate-arrow");
			$("#cs-schoollist-dropdown-list").slideUp(300, "swing");
		});

        $('.sw-mystart-dropdown.schoollist').on('keydown', function(e) { 
        // Trigger the click event from the keyboard 
          var code = e.which; 
          // 13 = Return, 32 = Space 
          if ((code === 13) || (code === 32)) { 
          	e.preventDefault();
            $(this).addClass('inner');
            $(".sw-dropdown-arrow",this).addClass("rotate-arrow");
            $("#cs-schoollist-dropdown-list").slideDown(300, "swing");
            $("#cs-schoollist-dropdown-list").attr("aria-hidden","false");
            $('.sw-dropdown a').first().delay(300).focus();
            //$(".sw-mystart-dropdown .sw-dropdown-list li a, #sw-myaccount-list li a").attr("tabindex","0");
          }

        });
        $('.schoollist .sw-dropdown a').keydown(function(e){
            var c = e.which;
            var next = $(this).parent().next().children();
            var prev = $(this).parent().prev().children();
            if(c == csKeyCodes.down || c == csKeyCodes.up){
                e.preventDefault();
                if(c == csKeyCodes.down && $(this).hasClass('last')){
                    $('.sw-mystart-dropdown.schoollist').focus().removeClass('inner');
                } else if(c == csKeyCodes.down){
                    next.focus();
                } else if(c == csKeyCodes.up && $(this).hasClass('first')){
                    $('.sw-mystart-dropdown.schoollist').focus().removeClass('inner');
                } else if(c == csKeyCodes.up) {
                    prev.focus();
                }
            }
        });

		$("#sw-mystart-account .sw-dropdown").attr("id","sw-myaccount-list").removeClass("sw-dropdown");
		$("#sw-mystart-account").off();
		$("#sw-mystart-account").click(function(){
			$(".sw-myaccount-chevron",this).addClass("rotate-arrow");
			$("#sw-myaccount-list").slideDown(300, "swing");
		});

		$("#sw-mystart-account").hover(function(){

		}, function(e) {
			e.stopPropagation();
			$(".sw-myaccount-chevron").removeClass("rotate-arrow");
			$("#sw-myaccount-list").slideUp(300, "swing");
		});

        $('#sw-mystart-account').on('keydown', function(e) { 
        // Trigger the click event from the keyboard 
          var code = e.which; 
          // 13 = Return, 32 = Space 
          if ((code === 13) || (code === 32)) { 
            $(".sw-myaccount-chevron",this).addClass("rotate-arrow");
			$("#sw-myaccount-list").slideDown(300, "swing");
            $("#sw-myaccount-list").attr("aria-hidden","false");
            $("#sw-myaccount-list li > a").attr("tabindex","0");
          }

        })

        //DOC change mystart buttons text

        	//SIGN IN & REGISTER DROPDOWN
            if($(".sw-mystart-button.signin").length) {
                var signinBtn = $(".sw-mystart-button.signin").html();
                var $signinDropdown = "
" + signinBtn + "";
                $(".sw-mystart-button.signin").hide();
            }
            if($(".sw-mystart-button.register").length) {
                var registerBtn = $(".sw-mystart-button.register").html();
                var $registerDropdown = "
" + registerBtn + "";
                $(".sw-mystart-button.register").hide();
            } else {
            	var $registerDropdown = "";
            }
            var $myAccountBtn = '
' +
                                '
User Options ' +
                                '' +
                                	$signinDropdown + $registerDropdown +
                                '' +
                               '';
            $("#sw-mystart-right div.clear").before($myAccountBtn);

            $("#sw-mystart-account").click(function() {
            	$(".sw-myaccount-chevron",this).addClass("rotate-arrow");
				$("#sw-myaccount-list").slideDown(300, "swing");
            });
            $("#sw-mystart-account").hover(function(){

            }, function(e) {
                e.stopPropagation();
                $(".sw-myaccount-chevron").removeClass("rotate-arrow");
                $("#sw-myaccount-list").slideUp(300, "swing");
            });



		//DOC #swsignin-txt-username -- AUTO-FOCUS USERNAME SIGNIN FORM FIELD #swsignin-txt-username
		$("#swsignin-txt-username").focus();

        //SEARCH
        $("#sw-mystart-search").prependTo("#gb-search-container");
        $(window).click(function() {
        	$("#gb-search-btn").attr("aria-label","Open Search Window");
            $("#gb-search-btn").removeClass("clicked");
            $("#gb-channel-list-outer").removeClass("show-search");
            $("#gb-search-container").removeClass("open");
        });
        $("#gb-search-btn").click(function(e) {
        	var windowHeight = $(window).height() - 100;
        	e.stopPropagation();
            if($(this).hasClass("clicked")) {
                $("#gb-search-btn").attr("aria-label","Open Search Window");
                $(this).removeClass("clicked");
                $("#gb-channel-list-outer").removeClass("show-search");
                $("#gb-search-container").removeClass("open");
                $("#gb-search-container").css("height","0");
            } else {
            	$("#gb-search-btn").attr("aria-label","Close Search Window");
                $(this).addClass("clicked");
                $("#gb-channel-list-outer").addClass("show-search");
                $("#gb-search-container").addClass("open");
        		$("#gb-search-container.open").css("height",windowHeight+"px");
                setTimeout(function () {
                	$("#sw-search-input").focus();
                }, 300);
            }
        });

