	$(function() {
    	CreativeTemplate.Init(); //tip of the iceberg
    });


    var CreativeTemplate = {

    	/*****
        	PROPERTIES
        *****/

        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "DefaultLogoSrc": "/cms/lib/PA50000097/Centricity/Template/17//images/hatboro-logo.png",
        "DropdownTimeout": "", //Will store the CustomDropdown timeout object that will get passed among the dropdown functions.
        "DynamicStyles": "", //This can be added to and if it's not empty it'll be added to the page in a stylesheet
        "MobileMenuBreakpoint": 768,
        "CustomChannels": "families,students,staff,community",



        /*****
        	METHODS
        *****/

// BIG BANG
        "Init": function() {
        	$('[data-hide=""], [data-hide="!false"], [data-hide="true"]').remove();

            this.SetTemplateProps();
            this.RsMenu();
            this.MyStart();
            this.Search();
            this.Header();
            this.CustomDropdowns();
            this.ChannelBar();
            this.Body();
            this.GlobalIcons();
            this.Slideshow(); //move this into JsMediaQueries if it isn't present across all of the Breakpoints
            this.AppAccordion();
            this.Footer();
            this.JsMediaQueries();

            if( this.DynamicStyles != "" ){ //Build out our stylesheet if any of the above items have added styles to it.
            	this.BuildStyleSheet();
            }

			csGlobalJs.OpenInNewWindowWarning();

            $(window)
            	.load(function(){ 
                	CreativeTemplate.WindowLoad(); 
				})
            	.resize(function(){ 
                     CreativeTemplate.WindowResize(); 
                     CreativeTemplate.CalendarListView();
                })
                .scroll(function(){
                    CreativeTemplate.WindowScroll();
                });
        },


// TOP LEVEL (Window/Document) EVENT HANDLERS
        "WindowLoad": function() {
            this.CalendarListView();
			this.Search();
            csGlobalJs.OpenInNewWindowWarning();

              // MAKE NAVIGATION APPS ACCESSIBLE
            $(".ui-widget.app.navigation").csAccessibleNavigationApp();
        },
        "WindowResize": function() {
            this.JsMediaQueries();
            this.CalendarListView();
        },
        "WindowScroll": function(){},


//TEMPLATE EVENT HANDLERS
        "JsMediaQueries": function() {
            if( this.GetBreakPoint() == "desktop" ){
            	//Desktop

                if( $('#sw-mystart-mypasskey').hasClass('moved-for-mobile') ){
                	$('#sw-mystart-mypasskey')
                    	.removeClass('moved-for-mobile')
                        .appendTo('#footer-mystart');
                }

                if( $('#footer-social-media').hasClass('moved-for-mobile') ){
					$('#footer-social-media')
                    	.removeClass('moved-for-mobile')
                        .appendTo('#gb-footer .footer-center.bottom');
                }

                if( $('.hp').length > 0 ){
                	if($('#hp-mmg-app-overlay').hasClass('moved-for-mobile')){
                    	$('#hp-mmg-app-overlay')
                        	.removeClass('moved-for-mobile')
                            .appendTo('#hp-slideshow');
                    }

                    if($('.upcomingevents .cs-article-controls').length > 0 && $('.upcomingevents .cs-article-controls.moved-for-mobile').length > 0){
                    	$('.upcomingevents').each(function(appIndex, app){
                        	if($('.cs-article-controls', app).length > 0){
                            	$('.cs-article-viewer', app).after($('.cs-article-controls', app).removeClass('moved-for-mobile'));
                            }
                        });
                    }

                    if($('.headlines .cs-article-slider-control').length > 0 && $('.headlines .cs-article-slider-control.moved-for-mobile').length > 0){
                        $('.headlines').each(function(appIndex, app){
                            if($('.cs-article-slider-control', app).length > 0){
                                $('.cs-article-viewer', app).before($('.cs-article-slider-control.back', app).removeClass('moved-for-mobile'));
                            }
                        });
                    }
                }

            }else{
            	//BP's

                if( !$('#sw-mystart-mypasskey').hasClass('moved-for-mobile') ){
                	$('#sw-mystart-mypasskey')
                    	.addClass('moved-for-mobile')
                        .appendTo('#gb-channel-bar');
                }

                if( !$('#footer-social-media').hasClass('moved-for-mobile') ){
					$('#footer-social-media')
                    	.addClass('moved-for-mobile')
                        .appendTo('#footer-address');
                }

                if( $('.hp').length > 0 ){
                	if(!$('#hp-mmg-app-overlay').hasClass('moved-for-mobile')){
                    	$('#hp-mmg-app-overlay')
                        	.addClass('moved-for-mobile')
                            .prependTo('#hp-content');
                    }

                    if( this.GetBreakPoint() == "320" ){
                        if($('.upcomingevents .cs-article-controls').length > 0 && $('.upcomingevents .cs-article-controls.moved-for-mobile').length <= 0){
                            $('.upcomingevents').each(function(appIndex, app){
                                if($('.cs-article-controls', app).length > 0){
                                    $('.ui-widget-footer', app).after($('.cs-article-controls', app).addClass('moved-for-mobile'));
                                }
                            });
                        }

                        if($('.headlines .cs-article-slider-control').length > 0 && $('.headlines .cs-article-slider-control.moved-for-mobile').length <= 0){
                            $('.headlines').each(function(appIndex, app){
                                if($('.cs-article-slider-control', app).length > 0){
                                	$('.cs-article-slider-control.next', app).before($('.cs-article-slider-control.back', app).addClass('moved-for-mobile'));
                                }
                            });
                        }
					} else {
                    	if($('.upcomingevents .cs-article-controls').length > 0 && $('.upcomingevents .cs-article-controls.moved-for-mobile').length > 0){
                            $('.upcomingevents').each(function(appIndex, app){
                                if($('.cs-article-controls', app).length > 0){
                                    $('.cs-article-viewer', app).after($('.cs-article-controls', app).removeClass('moved-for-mobile'));
                                }
                            });
                        }

                        if($('.headlines .cs-article-slider-control').length > 0 && $('.headlines .cs-article-slider-control.moved-for-mobile').length > 0){
                            $('.headlines').each(function(appIndex, app){
                                if($('.cs-article-slider-control', app).length > 0){
                                	$('.cs-article-viewer', app).before($('.cs-article-slider-control.back', app).removeClass('moved-for-mobile'));
                                }
                            });
                        }
                    }
                }
            }
        },
        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },
        "CustomDropdowns": function() {
        	/* customDropdowns
            **  Use: Sets up custom dropdown menus.
            */
            $('.custom-dropdown .dropdown a').off('keydown keyup keypress').keydown(function(e){
            	$(this).attr('tabindex', -1);
                if( e.keyCode == 9 ){
                    CreativeTemplate.ClearDropdowns();
                }else if( e.keyCode == 27){
                	e.preventDefault();
                	$('.custom-dropdown > button[aria-expanded="true"]').focus();
                    CreativeTemplate.ClearDropdowns();
				}
            });

        	$('.custom-dropdown input, .custom-dropdown a, .custom-dropdown .custom-dropdown > button').attr('tabindex', -1);
            $('.custom-dropdown button').on('keydown', function(e) { // Trigger the click event from the keyboard
                var code = e.which;
                // 13 = Return, 32 = Space
                if ((code === 13) || (code === 32)) {
                    e.preventDefault();

                    $(this).click();
                    if( !$(this).parent().hasClass('openMenu') ){
                        //clear our keyboard hooks since the menu is closing.
                        CreativeTemplate.DropdownKeyboardFunctionality($('~ .dropdown', this), true);
                    }else{
                        //add our keyboard hooks
                        CreativeTemplate.DropdownKeyboardFunctionality($('~ .dropdown', this));
                    }
                }else if(code == 39 || code == 40 && $(this).attr('aria-expanded') == 'true'){
                    e.preventDefault();
                    $('~ .dropdown a', this).first().focus();
                }else if( code == 9 ){
                	if($(this).attr('aria-controls') != 'translate-option-list'){
                		CreativeTemplate.ClearDropdowns();
					}
                }
            });

            //Custom Dropdown Functionality
            $('.custom-dropdown > button').off('click').click(function(e){
                clearTimeout(this.DropdownTimeout);

                if( $(this).attr("aria-expanded") == "false" ){
                    CreativeTemplate.ClearDropdowns()
                    $('~ .dropdown', this).attr('aria-hidden', 'false').slideDown();
                    $(this).attr('aria-expanded', 'true');
                    $(this).parent().addClass("openMenu");
                }else{
                    CreativeTemplate.ClearDropdowns();
                }
            });

            $('.custom-dropdown').hover(function(e){ //In
                clearTimeout(CreativeTemplate.DropdownTimeout);
            },function(e){ //Out
                //canDropDownHide = true;
                CreativeTemplate.DropdownTimeout = setTimeout(function(){
                    CreativeTemplate.ClearDropdowns();
                }, 800);
            });
        }, /* Self-Made Dropdown - Main Function and Event Handlers*/
        "ClearDropdowns": function(){
        	$('.custom-dropdown.openMenu input, .custom-dropdown.openMenu a, .custom-dropdown.openMenu .custom-dropdown > button').attr('tabindex', -1);
            $('.custom-dropdown.openMenu .dropdown').attr('aria-hidden', 'true').slideUp();
            $('.custom-dropdown.openMenu > button').removeClass('acc-open').attr('aria-expanded', 'false');
            $('.custom-dropdown.openMenu li').removeClass('active-list-item');
            this.DropdownKeyboardFunctionality($('.custom-dropdown.openMenu .dropdown'), true);
            $('.custom-dropdown.openMenu').removeClass("openMenu");
        }, /* Self-Made Dropdown - Clears the dropdowns */
        "DropdownKeyboardFunctionality": function(dropdown, clear){
        	if( clear ){
                dropdown.off('keydown');
            }else{
                $('li:first', dropdown).addClass('active-list-item');
                $('li.active-list-item a', dropdown).trigger('focus');

                    // 38 = up and 40 = down
                dropdown.keydown(function(e){
                    if(e.which == 38 || e.which == 40){
                        e.preventDefault();
                        switch( e.which ){
                            case 38:  //up
                                if( $('li.active-list-item', this).index() == 0){
                                    $(this).siblings('button').focus();
                                    $('li', this).removeClass('active-list-item');
                                    $('li:first', this).addClass('active-list-item');
                                }else{	
                                    var topItem = $('li.active-list-item', this).prev('li');
                                    if(topItem.hasClass('list-label')) topItem = topItem.prev('li');

                                    $('li.active-list-item', this).removeClass('active-list-item');
                                    topItem.addClass('active-list-item');
                                    $('a', topItem).focus();
                                }
                                break;
                            case 40:  //down
                                if( $('li.active-list-item', this).index() == $('li', this).length - 1 ){
                                    $('li', this).removeClass('active-list-item');
                                    $('li:first', this).addClass('active-list-item');
                                    $('li.active-list-item a', this).focus();
                                }else{
                                    var bottomItem = $('li.active-list-item', this).next('li');
                                    if(bottomItem.hasClass('list-label')) bottomItem = bottomItem.next('li');

                                    $('li.active-list-item', this).removeClass('active-list-item');
                                    $(bottomItem).addClass('active-list-item');
                                    $('a', bottomItem).focus();
                                }
                                break;
                            default:
                                //pass
                                break;
                        }
                    }
                });
            }
        }, /* Self Made Dropdown - a11y Event Handlers */
        "PageOverlayEvents": function(){
        	$('.page-overlay-trigger').off('click').click(function(e){
                if( $(this).attr('aria-expanded') == "false" ){
                    $(this)
                        .css('top', $(this).offset().top) /* Position the button exactly where it is so there is no movement at all from it's position. */
                        .css('right', $(window).width() - ($(this).offset().left + $(this).outerWidth()))
                        .attr('aria-expanded', 'true');
                    const targetPanel = $('#' + $(this).attr('aria-controls'));
                    targetPanel.attr('aria-hidden', 'false');

                    $('a, input', targetPanel).first().focus();
                }else{
                    setTimeout(function(){ 
                        $('.page-overlay-trigger[aria-expanded="true"]')
                            .css('top', '')
                            .css('right', '')
                            .attr('aria-expanded', 'false'); 
                    }, 300);

                    $('#' + $(this).attr('aria-controls')).attr('aria-hidden', 'true');
                }
            });

            //a11y

			$('.page-overlay a, .page-overlay input').attr('tabindex', -1);

            $('.page-overlay-trigger').off('keydown').keydown(function(e){
            	if( $(this).attr('aria-expanded') == "true" ){
                    switch(e.keyCode){
                        case CreativeTemplate.KeyCodes.tab:
                        case CreativeTemplate.KeyCodes.down:
                        case CreativeTemplate.KeyCodes.right:
                            e.preventDefault();
                            if($(this).attr('aria-controls').indexOf('search') >= 0){
                            	$('#' + $(this).attr('aria-controls') + ' input').first().focus();
                            }else{
                            	$('#' + $(this).attr('aria-controls') + ' a').first().focus();
							}
                            break;

                        case CreativeTemplate.KeyCodes.up:
                        case CreativeTemplate.KeyCodes.left:
                            e.preventDefault();
                            if($(this).attr('aria-controls').indexOf('search') >= 0){
                            	$('#' + $(this).attr('aria-controls') + ' input').last().focus();
                            }else{
                            	$('#' + $(this).attr('aria-controls') + ' a').last().focus();
							}
                            break;
                        case CreativeTemplate.KeyCodes.esc:
                        	if( $(this).attr('aria-expanded') == "true" ){
                            	$(this).click();
                            }
                        	break;
                    }
            	}
            });

            $('#site-shortcut-overlay a').off('keydown').keydown(function(e) {
                var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };
                var linkObj = $('#site-shortcut-overlay a');

                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                        // IS FIRST ITEM
                        if(linkObj.index($(this)) == 0) {
                            $('#gb-site-shortcut-overlay-trigger').focus();
                        } else {
                            linkObj[linkObj.index($(this)) - 1].focus();
                            //$(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                        // IS LAST ITEM
                        if(linkObj.index($(this)) == (linkObj.length - 1) ) {
                            // FOCUS FIRST ITEM
                            linkObj[0].focus();
                        } else {
                            // FOCUS NEXT ITEM
                            linkObj[linkObj.index($(this)) + 1].focus();
                        }
                    break;
                    case csKeyCodes.tab:
                    	e.preventDefault();
                        e.stopImmediatePropagation();

                    	$('#gb-site-shortcut-overlay-trigger').focus();
                    	break;
                }
            });

            $('#search-overlay input').off('keydown').keydown(function(e) {
                var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };
                var linkObj = $('#search-overlay input');

                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                        // IS FIRST ITEM
                        if(linkObj.index($(this)) == 0) {
                            $('#gb-search-trigger').focus();
                        } else {
                            linkObj[linkObj.index($(this)) - 1].focus();
                            //$(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                        // IS LAST ITEM
                        if(linkObj.index($(this)) == (linkObj.length - 1) ) {
                            // FOCUS FIRST ITEM
                            linkObj[0].focus();
                        } else {
                            // FOCUS NEXT ITEM
                            linkObj[linkObj.index($(this)) + 1].focus();
                        }
                    break;
                    case csKeyCodes.tab:
                    	e.preventDefault();
                        e.stopImmediatePropagation();

                    	$('#gb-search-trigger').focus();
                    	break;
                }
            });

            $('.page-overlay').off('keydown').keydown(function(e) {
            	if( e.keyCode == 27 ){
                	e.preventDefault();
                    $('.page-overlay-trigger[aria-controls="' + $(this).attr('id').replace('#', '') + '"]').click().focus();
                }
            });
        },


// SETTERS
        "SetTemplateProps": function() {
            // MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },
        "BuildStylesheet": function() {
        	var dynStyleSheet = document.createElement('style');

            if(dynStyleSheet) {
                dynStyleSheet.setAttribute('type', 'text/css');
                var head = document.getElementsByTagName('head')[0];

                if(head) {
                    head.insertBefore(dynStyleSheet, head.childNodes[0]);
                }

                var rules = document.createTextNode(this.DynamicStyles);

                if(dynStyleSheet.styleSheet){ // IE
                    dynStyleSheet.styleSheet.cssText = this.DynamicStyles;
                } else {
                    dynStyleSheet.appendChild(rules);
                }
            }
        },


// GETTERS
        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }, 
        "HexToRgb": function(hex){
        	var r = parseInt((this.CutHex(hex)).substring(0,2),16);
	        var g = parseInt((this.CutHex(hex)).substring(2,4),16);
	        var b = parseInt((this.CutHex(hex)).substring(4,6),16);
	        return r+', '+g+', '+b;
        },
        "CutHex": function(hex){
        	return (h.charAt(0)=="#") ? hex.substring(1,7):hex;
        },


