        "ChannelBar": function() {
            //Overwrite system hover functionality for an animated version.
            $('#channel-navigation > .sw-channel-item > a').off('touchstart');
            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){ //in
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.stop(true, true).slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){ //out
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

            var channelFound = 0;

            setTimeout(function(){
                CreativeTemplate.MegaMenu();

                //DOC check if channel dropdown goes off screen
                $("#gb-channel-bar .sw-channel-item.mega-channel").on('mouseenter mouseleave', function (e) { 
                    if ($('ul.sw-channel-dropdown', this).length) {
                        $("ul.sw-channel-dropdown", this)
                            .css('left', '')
                            .show();

                        var elm = $('.sw-channel-dropdown', this);
                        var off = elm.offset();
                        var l = off.left;
                        var w = elm.width();
                        var docH = $("#sw-channel-list-container").height();
                        var docW = $("#sw-channel-list-container").width();
                        var winW = $(window).width() - 35;
                        var overflow = -((l + w) - winW + 35);

                        var isEntirelyVisibleR = (l + w <= winW);

                        if (!isEntirelyVisibleR) {
                            $(this).addClass('edge');

                            $("#gb-channel-bar .mega-channel.sw-channel-item.edge ul.sw-channel-dropdown").css("left", overflow);
                        } else {
                            $(this).removeClass('edge');
                        }
                    }
                });

                //since we are here lets adjust the breadcrumbs as well
                $('#sp-breadcrumbs .ui-breadcrumbs li > a').after('');

                $('#gb-channel-bar .sw-channel-item').each(function(channelIndex, channel){
                    if( CreativeTemplate.CustomChannels.indexOf($('> a', channel).text().trim().toLowerCase()) >= 0 ){
                        $('#channel-navigation-two').append($(channel));
                        channelFound = 1;
                    }
                });

                if(channelFound){
                    $('#gb-channel-bar-two-items').show(400);
                }else{
                	$('#gb-channel-bar-two-items').remove();
                }

                $('#sw-channel-list-container').show(400);

                $("#gb-channel-bar-two .sw-channel-item.mega-channel").on('mouseenter mouseleave', function (e) { 
                    if ($('ul.sw-channel-dropdown', this).length) {
                        $("ul.sw-channel-dropdown", this)
                            .css('right', '')
                            .show();

                        var elm = $('.sw-channel-dropdown', this);
                        var off = elm.offset();
                        var l = off.left;
                        var overflow = l - 5;

                        var isEntirelyVisibleL = (l > 5);

                        if (!isEntirelyVisibleL) {
                            $(this).addClass('edge');
                            $("#gb-channel-bar-two .mega-channel.sw-channel-item.edge ul.sw-channel-dropdown").css("right", overflow);
                        } else {
                            $(this).removeClass('edge');
                        }
                    }
                });

        	}, 300);
        },
        "Body": function() {
            // AUTO FOCUS SIGN IN FIELD
