');
                $(".ui-breadcrumbs").append("
Search Results");
            }

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $("div.ui-widget.app .ui-widget-detail img")
                .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
                .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            if( $('#hp-mmg-app-overlay .region').children().length <= 0 ) $('#hp-mmg-app-overlay').hide();

            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home");

            $('div.app-level-social-follow:empty').remove();
            $('.ui-widget-footer .clear').remove();

           	// ADJUST MORE LINKS
            $("div.ui-widget.app").each(function(appIndex, app){
            	//more link
                if( $('.more-link', app).length > 0 ){
                    $('.more-link', app).prependTo( $('.ui-widget-footer', app) );
                    $('.more-link-under', app).parent().remove();
                }

                //view calendar link
                if( $('.view-calendar-link', app).length > 0 ){
                    $('.view-calendar-link', app).prependTo( $('.ui-widget-footer', app) );
                }

                //Hide empty footer
                if( $('.ui-widget-footer', app).children().not('.clear').length <= 0 ){
                	$('.ui-widget-footer', app).addClass('no-children').hide();
                }else{
                	$('.ui-widget-footer', app).addClass('has-children');
                }
            });

            // USE CHANNEL NAME FOR PAGELIST NAV HEADER IF ONE IS NOT PRESENT
            if(!$("div.sp.column.one .ui-widget-header h1").length) {
                $("div.sp.column.one .ui-widget-header").append("
");
            }

            //this is so that we can build CSS selectors based off of the last row and because the styling needed applied in a certain way that CSS couldn't muster.
            $('.hp .custom-site-shortcuts .siteshortcuts').each(function(appIndex, app){
            	var lastRow = $('.ui-widget-detail > ul > li', app).length%3;
                lastRow = (lastRow > 0) ? lastRow : 3;

                $('.ui-widget-detail > ul > li:nth-last-child(-n+' + lastRow + ')', app).addClass('last-row');
            });

            $(".hp-row").each(function(rowIndex, row){
            	if( $('.hp-column', row).length <= 0 ){
                	if( $('.region', row).children().length <= 0 ){
                        $(row).hide();
                    }
                }else{
                	var cols = $('.hp-column', row).length
                    var colsHidden = 0;

                	$('.hp-column', row).each(function(colIndex, col){
                    	if( $('.region', col).children().length <= 0 ){
                        	colsHidden++;
                            $(col).hide();
                        }
                    });

                    if( colsHidden == cols ){
                    	$(row).hide();
                    }
                }
            });


            //Customize Apps
            this.UpcomingEvents();
            var subpage = ($('.sp').length > 0) ? 1 : '';
            this.SiteShortcutDropdown(subpage); //also builds the click event for the search dropdown

            this.CustomHeadlines();
        },
