            $("#gb-channel-bar #sw-channel-list-container li.sw-channel-item:not(#navc-HP)").each(function(i, li){
                //REPLACE ANY SECTION THAT CONTAINS & WITH AND
                $(".sw-channel-dropdown > li > a > span", this).text(function () {
                    return $(this).text().replace("&", "and"); 
                });

                if( i < megaMenuData.length ){
                    if( megaMenuData[i].channelCaption != "" || megaMenuData[i].channelImgBool != 'false'){
                        // ADD MEGA CHANNEL CLASS
                        $(this).addClass('mega-channel');
                        $(this).addClass("Channel-" + i);


                        // ADD CHANNEL CATEGORIES
                        var categoryColumn = '
';
                        var textNums = ["one", "two", "three", "four", "five", "six", "seven", "eight", "nine", "ten", "eleven", "twelve", "thirteen", "fourteen", "fifteen", "sixteen", "seventeen", "eighteen", "nineteen", "twenty"];
                        var catCol = 1;
                        if($("> .sw-channel-dropdown li", this).length > 2){
                            catCol = 3;
                        }else if( $("> .sw-channel-dropdown li", this).length > 1){
                            catCol = 2;
                        }

                        categoryColumn += '
';

                        categoryColumn +=   '';

                        $("> .sw-channel-dropdown li", this).each(function(liIndex, li){
                            categoryColumn += '
' + $(li).text() + '';
                            } else {
                                categoryColumn += '>' + $(li).text() + '';
                            }
                        });

                        $(this).addClass('cols-' + catCol);

                        categoryColumn +=	'';

                        // ADD STRUCTURE TO THE DOM
                        $(".sw-channel-dropdown", this).html('




');

                        // ADD IMAGE
                        var imgColumn = '';
                        var imgSrc = jQuery.trim(megaMenuData[i].channelImg);
                        var srcSplit = imgSrc.split("/");
                        var srcSplitLen = srcSplit.length;
                        var noImg = false;

                        if(megaMenuData[i].channelImgBool != "false" && imgSrc != "" && srcSplit[srcSplitLen - 1] != "default-man.jpg") {
                            $(".sw-channel-dropdown .mega-columns .mega-menu-bottom").addClass('has-thumb');
                            imgColumn = '';
                            $(".sw-channel-dropdown .mega-columns .mega-menu-bottom .mega-menu-bottom-col-one", this).append(imgColumn);
                        }else{
                            noImg = true;
                            $(this).addClass('no-img');
                        }


                        // ADD CAPTION
                        var noCaption = false;
                        if(jQuery.trim(megaMenuData[i].channelCaption) != "" ){
                            var captionColumn = '
' + jQuery.trim($(' > a ', li).text()) + '
' + jQuery.trim(megaMenuData[i].channelCaption) + '';
                            $(".sw-channel-dropdown .mega-columns .mega-menu-bottom .mega-menu-bottom-col-two", this).append(captionColumn);
                        }else{
                            noCaption = true;
                        }

                        if(noCaption){
                            $(this).addClass('no-text');
                            $(".sw-channel-dropdown .mega-columns .mega-menu-bottom .mega-menu-bottom-col-two", this).remove();

                            if(noImg){
                                $('.sw-channel-dropdown .mega-menu-bottom', this).remove();
                                $('.sw-channel-dropdown .mega-columns', this).addClass('full-width');
                            }else{
                                $('.sw-channel-dropdown .mega-columns', this).addClass('flex-cont');
                            }
                        }

                        $(".sw-channel-dropdown .mega-columns .mega-menu-top", this).append(categoryColumn);

                        $('.sw-channel-dropdown .mega-channel-cat-outer li').addClass('mega-channel-cat-section-list-item');

					}
                 }else{
                 	$(li).addClass('no-mega');
                 }
            }); 

            $('.mega-channel .sw-channel-dropdown a').off('keydown').keydown(function(e) {
                var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };
                var linkObj = $('.mega-channel .sw-channel-dropdown[aria-hidden="false"] a');

                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                        // IS FIRST ITEM
                        if(linkObj.index($(this)) == 0) {
                            $('.sw-channel-item.hover > a').focus();
                        } else {
                            linkObj[linkObj.index($(this)) - 1].focus();
                            //$(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                        // IS LAST ITEM
                        if(linkObj.index($(this)) == (linkObj.length - 1) ) {
                            // FOCUS FIRST ITEM
                            linkObj[0].focus();
                        } else {
                            // FOCUS NEXT ITEM
                            linkObj[linkObj.index($(this)) + 1].focus();
                        }
                    break;
                }
            });
        },


