        "CustomHeadlines": function(){
        	$('.custom-headlines .headlines .ui-widget-detail').wrap('
');

        	$('.custom-headlines .headlines').each(function(appIndex, app){
            	//mark the old detail and then clone it so we have a list and an slider detail
            	$('.ui-widget-detail', app).addClass('list-detail').after($('.ui-widget-detail', app).clone().removeClass('list-detail').addClass('slider-detail'));
                $('.ui-widget-detail.slider-detail', app).attr('id', $('.ui-widget-detail.slider-detail', app).attr('id') + '-2');

                $('.ui-widget-detail.list-detail', app).wrap('
');
                // remove all of the items that are going into the slider
                $('.ui-widget-detail.list-detail .ui-articles > li', app).slice(0, 4).remove();

                if( $('.ui-widget-header', app).children().length > 0 || $('.ui-widget-footer.has-children', app).length > 0 ){
                    $('.app.headlines-list', app).prepend($('.ui-widget-header', app));

                    $('.ui-widget-footer.has-children', app).appendTo($('.app.headlines-list', app));

                    if( $('.ui-widget-detail.list-detail .ui-articles > li', app).length <= 0 ){
                    	$('.ui-widget-detail.list-detail', app).addClass('no-content');
                    }
                }else if( $('.ui-widget-detail.list-detail .ui-articles > li', app).length <= 0 ){
                    $('.ui-widget-detail.list-detail', app).remove();
                }


                $('.ui-widget-detail.slider-detail', app).wrap('
');
                //remove all of the items that shouldn't be in the slider
                $('.ui-widget-detail.slider-detail .ui-articles > li:nth-child(1n+' + (4 + 1) + ')', app).remove();
                if( $('.ui-widget-detail.slider-detail .ui-articles > li', app).length <= 0 ){
                	$('.ui-widget-detail.slider-detail', app).remove();
				}else{
                	//build the article slider
                	$(".ui-widget-detail-wrapper .headlines-slider", app).csArticleSlider({
                        "viewerNum": [2, 2, 2, 1, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                        "transitionSpeed": .5 // SECONDS
                    });
                }
            });
        }
