$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=L1%2bHO8i9Jg%2bmxzr%2bDaQdZQ%3d%3d&SessionID=972f1d14-e3dd-4356-9ef7-31f2a3beb0c0&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f29%2f2020+10%3a21%3a16+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.86.195';
