$('#navigation').slicknav({
    label: 'Menu',
    prependTo: '#mobile_top_nav',
    allowParentLinks: true,
});
var navTop = $("nav").offset().top
$(window).scroll(function(){
    if (navTop <= $(window).scrollTop()){
        $("nav").css("position", "fixed");
        $("nav").css("top", "0");
    }else{
        $("nav").css("position", "static");
    }
});

function QuickLinks() {
    $('.expand-one').click(function () {
        $('.content-one').slideToggle('slow');
        switch ($('.Toggle').attr('class')) {
            case 'Toggle PlusImage':
                $('.Toggle').addClass('XImage');
                $('.Toggle').removeClass('PlusImage')
                break;
            case 'Toggle XImage':
                $('.Toggle').addClass('PlusImage');
                $('.Toggle').removeClass('XImage')
                break;
            default: $('.Toggle').addClass('XImage');
                break;
        }
    });
}
$(document).ready(function () {
    QuickLinks();
});
