        currentPagePath: '/content/schools/ispa/en',
        label: 'News',
        listFrom: 'solr',
        limit: '10',
        listType: 'news',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/news.html',
        viewAllTitle: 'View All',
        uuid: '8f925a51-dfde-4596-847d-d8e136d153d9',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

