function resizeSlider_ir5590() {

	var windowWidth = parseFloat(document.body.offsetWidth);
	var containerWidth = windowWidth;

	var contentWidth = parseFloat("1100");
	if(contentWidth==0) {
		contentWidth = containerWidth;
	}

	if(windowWidth<contentWidth) {
		containerWidth = contentWidth;
	}

	document.getElementById("ir5590-ir5590-slider").style.width = containerWidth + "px";
	var fadeSlice = document.getElementById("nivo-slice-0");
	if(fadeSlice!=null) {
		fadeSlice.style.width = containerWidth + "px";
	}
	// document.getElementById("ir5590-ir5590-slider").style.height = Math.max((parseFloat("640")/parseFloat("1200") * containerWidth), parseFloat("640")) + "px";
	document.getElementById("ir5590-ir5590-slider").style.marginLeft = ((contentWidth - containerWidth) / 2.0) + "px";

}

