							return a.fullDate - b.fullDate;
						});

						// Check for duplicate events
						events = getRecID(events);
						events = findDuplicates(events);



						function findDuplicates(data) {
							let idArray = [];
							for(let i=0; i<data.length; i++){
								let currentID = String(data[i].recID);
								if(idArray.indexOf(currentID) == -1) {
									idArray.push(data[i].recID);
								} else {
									data.splice(i, 1);
									i--;
								}
							}
							return data
						}

						function getRecID(data) {
							data.forEach(function(element, index){
								let str = element.url;
								str = str.split("REC_ID=")[1];
								element.recID = str.substring(0, 7);
							});
							return data;
						}

