Date: " + event.monthNameShort + " " + event.day + " " + event.time + "";

							if (event.location != '') {
								eventsHTML += "
Location:" + event.location + "";
							}
							eventsHTML += "";
						}
						eventsHTML += "
Show All Events";

						document.getElementById("events_container").innerHTML = eventsHTML;
