function hideappearingChat() {
        let chat = document.querySelector('.open-chat');
        let btnClose = document.querySelector('.open-chat__close');

        if (chat) {
            btnClose.onclick = function () {
                chat.style.display = 'none';
            }
        }
    }

    hideappearingChat();
