div.script:
$(document).ready(function(){
	$.simpleWeather({
		location: 'Honesdale PA',
		unit: 'f',
		woeid: '',
		success: function(weather){
			var html = '

Honesdale PA '+weather.temp+'°'+weather.units.temp+'';
			$("#easyWeather").html(html); 
		},
		error: function(error){
			$("#easyWeather").html('
'+error+''); 
		}
	});
});

