			$$('div.blog-social div.fb-like').each(function(div) {
				div.className = 'blog-social-item blog-fb-like';
			});
			$$('#commentArea iframe').each(function(iframe) {
				iframe.style.minHeight = '410px';
			});
		}
	}
	catch(ex) {}
})(window._W && _W.jQuery);
