		        $("#menu").css("left" , -getWidth);	
			var menuStatus;
			$("a.slide-out-menu").click(function(){
				if(menuStatus != true){
					$("#gb-page, #sw-footer-outer").animate({
					left: getWidth,
					}, 300, function(){
						menuStatus = true
					});
					return false;
				} else {
