			var getWidth = $(window).width();
			var menuStatus;
			$("a.slide-out-menu").click(function(){
				if(menuStatus != true){
					$("#menu").animate({
						width: getWidth
					}, 300, function(){
						menuStatus = true;
					});
					return false;
				} else {
					$("#menu").animate({
						width: 0
					}, 300, function(){
						menuStatus = false;
					});
					return false;
				}
			});

			// SLIDE OUT MENU
	        	$("#sw-channel-list-container ul").appendTo("#menu .menu-option.channels");
	        	$("#sw-myaccount-list").appendTo("#menu .menu-option.account");
	        	$("div.sw-mystart-dropdown.schoollist ul").appendTo("#menu .menu-option.schools");
	        	$("#gb-channel-list-outer").remove();
	        }
	}

	function runFunctions() {
		var getWidth = $(window).width();
		var channelCode = $("#gb-channel-list-outer").html();

		if (getWidth >= 1024) { // 960+
			if ($("#gb-page #gb-channel-list-outer").length > 0){
				$("#gb-page #gb-channel-list-outer").remove();
			}
			$("#gb-header #gb-channel-list-outer").show();

		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			$("#gb-header #gb-channel-list-outer").hide();
			if ($("#gb-page #gb-channel-list-outer").length < 1){
				$("#gb-page").prepend('
' + channelCode + "");
			}
			resetIconWidths768();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			iconSetWidths();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			iconSetWidths();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			iconSetWidths();
		}
	}

	function dynamicContent() {
		//DOC fixes search box not allowing spaces
		$("#sw-mystart-search").unbind();

		//DOC #sp-breadcrumbs -- ELEMENT breadcrumbs SHOWS OR HIDES BREADCRUMBS
		var breadcrumbs = 'true';
		if(breadcrumbs == "false"){
			$("ul.ui-breadcrumbs").hide();
		}

		// #gb-logo -- ELEMENT showSchoolLogo SHOWS OR HIDES SCHOOL LOGO
		var showLogo = 'true'; 
		if(showLogo == "true") {
			$("#gb-logo").html('');
		}
        var feedbackText = $("div#sw-footer-links li:eq(0) a").text();
		var termsURL = $("div#sw-footer-links li:eq(2) a").attr("href");
		var termsText = $("div#sw-footer-links li:eq(2) a").text();
		var policyURL = $("div#sw-footer-links li:eq(1) a").attr("href");
		var policyText = $("div#sw-footer-links li:eq(1) a").text();
		var copyrightText = $("#sw-footer-outer #sw-footer-copyright").text();
		$(".cs-sw-footer-logo").html($("#sw-footer-logo").html());
		$("#cs-sw-footer-links-inner").html("" + termsText + "" + feedbackText + "" + policyText + "");
        $("" + copyrightText + "").appendTo("#cs-sw-footer-links");
		$("a.more-link span, a.view-calendar-link span").html(""); 
		if ($(".pagenavigation div.ui-widget-header h1").length < 1){
			$(".pagenavigation div.ui-widget-header").html("
")
		}
		//DOC #gb-header -- shows or hides Watermark
		var watermark = 'true';
		if (watermark == "true"){
			$("div#gb-header").css("background-image", "url('/cms/lib/SD01916852/Centricity/Template/17/gb-header.png')"); 
		}

		if($(window).width() >= 768 && $(".hp").length) { sameHeight(); }

		//HIDE SCHOOL NAME CONTAINER
		var nameToggle = 'true';
		var taglineToggle = 'true';

		if(nameToggle == "false" && taglineToggle == "false") {
			$("#gb-sitename").hide();
		}

		//APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
		$("div.ui-widget.app .ui-widget-detail img")
        .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
        .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
        .not($("div.ui-widget.app.headlines .ui-widget-detail img"))
        .each(function() {
          if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined || $(this).parent('p').attr('style') == 'text-align: center;' ) { // IMAGE HAS INLINE DIMENSIONS
              $(this).css({"width": $(this).attr("width") + "px", "max-width": "100%", "height": "auto", "max-height": $(this).attr("height") + "px"});
          }
        });

		//change questions & feedback link to pull from contact tab email address
		$(".gb.footer.three ul > li").first().addClass("cs-feedback-link");
		$(".cs-feedback-link > a").attr("href","mailto:MICHAEL.PARRY@K12.SD.US");
        }

