	function iconSetWidths(){
		// GET NUMBER OF ICONS
		var iconNum = $("a.global-icon").length;

		// DO SOME MATH TO FIGURE OUT WHAT ICON WIDTH SHOULD BE
		var iconWidth = 100 / iconNum;

		// SET ICON WIDTH
		$("a.global-icon").css("width", iconWidth + "%");
	}

	function resetIconWidths768(){
		$("a.global-icon").css("width", "70px");
	}

        function searchText() {
	        // SEARCH TEXT
	        //DOC #sw-mystart-search -- SETS THE VALUE OF #sw-search-input
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "Search...");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search...") {
				$(this).val("");
			}
		});
		//DOC #sw-mystart-search -- RESETS THE VALUE OF #sw-search-input
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search...");
			}
		});
	}

