    $( window ).resize( function(){
        if( $('#cboxOverlay' ).css( 'visibility' ) ){
        $(".group868242").colorbox.resize({ innerWidth: '80%', scalePhotos: 'true'});
    }
});
var $gallery868242 = $("a[rel=group868242]").colorbox({maxWidth: "80%"});
$("a#openGallery868242").click(function(e){
    e.preventDefault();
    $gallery868242.eq(0).click();
});

