div.script:
//event listener: DOM ready
function addLoadEvent(func) {
    var oldonload = window.onload;
    if (typeof window.onload != 'function') {
        window.onload = func;
    } else {
        window.onload = function () {
            if (oldonload) {
                oldonload();
            }
            func();
        }
    }
}
//call plugin function after DOM ready
addLoadEvent(function () {
    outdatedBrowser({
        bgColor: '#f25648',
        color: '#ffffff',
        lowerThan: 'boxShadow',
        languagePath: ''
    })
});

$(document).ready(function() {
  $( '.carousel-inner').find('.item:first' ).addClass( 'active' );
  $( 'ol.carousel-indicators' ).find('li:first').addClass( 'active' );
  $('.carousel').carousel({interval: 7000});

  $('#currentSmallEvent').val(0);
  $('#currentNews').val(0);
  $('#currentEntry').val(0);


var ev = getEventVar();
var nw = getNewsVar();
var be = getBlogVar();

    if (ev.cntr.length > 0 && ev.total > 3) {
        setSmallEvents(ev.start, ev.end);
        ev.cntr.removeClass('hidden');
        $('.smallEventPrev').children('.icon-left').addClass('inactive-icon');
        if (ev.total == ev.toShow) {
            $('.smallEventNext').children('.icon-right').addClass('inactive-icon');
        }

    }

	if (nw.cntr.length > 0 && nw.total > 3) {
        setNews(nw.start, nw.end);
        nw.cntr.removeClass('hidden');
        $('.smallNewsPrev').children('.icon-left').addClass('inactive-icon');
        if (nw.total == nw.toShow) {
            $('.smallNewsNext').children('.icon-right').addClass('inactive-icon');
        }

    }

    function getEventVar() {
        var evS = parseInt($('#currentSmallEvent').val());
        var evTS = parseInt($('#smallEventsToShow').val());
		var evE = evTS + evS;
        var evT = parseInt($('#totalEvents').val());
        var evC = $('.small-event-container');
        return {
            start: evS,
            end: evE,
			toShow: evTS,
            total: evT,
            cntr: evC
        };
    }

	function getNewsVar() {
		var nS = parseInt($('#currentNews').val());
        var nTS = parseInt($('#newsToShow').val());
		var nE = nTS + nS;
        var nT = parseInt($('#totalNews').val());
        var nC = $('#home-districtnews > .container > .row-content');
        return {
            start: nS,
            end: nE,
			toShow: nTS,
            total: nT,
            cntr: nC
        };
	}

    $('.smallEventPrev').click(function smallEventPrev() {
        var ev = getEventVar();
        if (ev.start !== 0) {
            ev.start--;
            ev.end--;
            setSmallEvents(ev.start, ev.end);
            if (ev.start == 0) {
                $('.smallEventPrev').children('.icon-left').removeClass('inactive-icon').addClass('inactive-icon');
            }
            if (ev.end < ev.total) {
                $('.smallEventNext').children('.icon-right').removeClass('inactive-icon');
            }
        }
    });
    $('.smallEventNext').click(function smallEventNext() {
        var ev = getEventVar();
        if (ev.end < ev.total) {
            ev.start++;
            ev.end++;
            setSmallEvents(ev.start, ev.end);
            if (ev.end == ev.total) {
                $('.smallEventNext').children('.icon-right').removeClass('inactive-icon').addClass('inactive-icon');
            }
            if (ev.start > 0 && ev.total > ev.toShow) {
                $('.smallEventPrev').children('.icon-left').removeClass('inactive-icon');
            }
        }
    });

    $('.smallNewsPrev').click(function smallNewsPrev() {
        var nw = getNewsVar();
        if (nw.start !== 0) {
            nw.start--;
            nw.end--;
            setNews(nw.start, nw.end);
            if (nw.start == 0) {
                $('.smallNewsPrev').children('.icon-left').removeClass('inactive-icon').addClass('inactive-icon');
            }
            if (nw.end < nw.total) {
                $('.smallNewsNext').children('.icon-right').removeClass('inactive-icon');
            }
        }
    });
    $('.smallNewsNext').click(function smallNewsNext() {
        var nw = getNewsVar();
        if (nw.end < nw.total) {
        nw.start++;
            nw.end++;
            setNews(nw.start, nw.end);
            if (nw.end == nw.total) {
                $('.smallNewsNext').children('.icon-right').removeClass('inactive-icon').addClass('inactive-icon');
            }
            if (nw.start > 0 && nw.total > nw.toShow) {
                $('.smallNewsPrev').children('.icon-left').removeClass('inactive-icon');
            }
        }
    });



    function setSmallEvents(evStart, evEnd) {
        var ev = getEventVar();
        //var evClass = 'col-xs-4 col-sm-4 col-md-4 col-lg-3';
        var evColMid = 'hidden-xs col-sm-3 col-md-2 col-lg-2';
		var evColEnd = 'hidden-xs hidden-sm col-md-2 col-lg-2'
        var evHidden = 'col-xs-6 col-sm-3 col-md-2 col-lg-2 hidden';
        ev.cntr.children('div').removeClass().addClass(evHidden);
        ev.cntr.children('div').slice(evStart, evStart + 2).removeClass('hidden');
		ev.cntr.children('div').slice(evEnd - 4, evEnd - 2).removeClass().addClass(evColMid);
		ev.cntr.children('div').slice(evEnd - 2, evEnd).removeClass().addClass(evColEnd);
        $('#currentSmallEvent').val(evStart);
    }

	function setNews(nStart, nEnd) {
        var nw = getNewsVar();
        var nColMid = 'hidden-xs col-sm-6 col-md-4 col-lg-4';
		var nColEnd = 'hidden-xs hidden-sm col-md-4 col-lg-4'
        var nHidden = 'col-xs-12 col-sm-6 col-md-4 col-lg-4 hidden';
        nw.cntr.children('div').removeClass().addClass(nHidden);
        nw.cntr.children('div').slice(nStart, nStart + 1).removeClass('hidden');
		nw.cntr.children('div').slice(nStart + 1, nStart + 2).removeClass().addClass(nColMid);
		nw.cntr.children('div').slice(nEnd - 1, nEnd).removeClass().addClass(nColEnd);
        $('#currentNews').val(nStart);
    }

	if (be.cntr.length > 0 && be.total > 3) {
        setBlog(be.start, be.end);
        be.cntr.removeClass('hidden');
        $('.blogPrev').children('.icon-left').addClass('inactive-icon');
        if (be.total == be.toShow) {
            $('.blogNext').children('.icon-right').addClass('inactive-icon');
        }

    }


function getBlogVar() {
		var bS = parseInt($('#currentEntry').val());
        var bTS = parseInt($('#entriesToShow').val());
		var bE = bTS + bS;
        var bT = parseInt($('#totalEntries').val());
        var bC = $('.inside-fisd-container');
        return {
            start: bS,
            end: bE,
			toShow: bTS,
            total: bT,
            cntr: bC
        };
	}


$('.blogPrev').click(function blogPrev() {
        var be = getBlogVar();
        if (be.start !== 0) {
            be.start--;
            be.end--;
            setBlog(be.start, be.end);
            if (be.start == 0) {
                $('.blogPrev').children('.icon-left').removeClass('inactive-icon').addClass('inactive-icon');
            }
            if (be.end < be.total) {
                $('.blogNext').children('.icon-right').removeClass('inactive-icon');
            }
        }
    });
    $('.blogNext').click(function blogNext() {
        var be = getBlogVar();
        if (be.end < be.total) {
        be.start++;
            be.end++;
            setBlog(be.start, be.end);
            if (be.end == be.total) {
                $('.blogNext').children('.icon-right').removeClass('inactive-icon').addClass('inactive-icon');
            }
            if (be.start > 0 && be.total > be.toShow) {
                $('.blogPrev').children('.icon-left').removeClass('inactive-icon');
            }
        }
    });



function setBlog(bStart, bEnd) {
        var be = getBlogVar();
        var bColMid = 'hidden-xs col-sm-6 col-md-4 col-lg-4';
	var bColEnd = 'hidden-xs hidden-sm col-md-4 col-lg-4'
        var bHidden = 'col-xs-12 col-sm-6 col-md-4 col-lg-4 hidden';
        be.cntr.children('div').removeClass().addClass(bHidden);
        be.cntr.children('div').slice(bStart, bStart + 1).removeClass('hidden');
		be.cntr.children('div').slice(bStart + 1, bStart + 2).removeClass().addClass(bColMid);
		be.cntr.children('div').slice(bEnd - 1, bEnd).removeClass().addClass(bColEnd);
        $('#currentEntry').val(bStart);
    }


  });

