div.script:
(function () {
    // Store the meta element
    var viewportMeta = document.getElementById('viewport-meta');

    // Get the viewport's content as an array
    const viewportContent = viewportMeta.getAttribute('content').split(',')

    // Convert its content attributes to an object
    const viewportAttributes = {}
    viewportContent.forEach((attribute) => {
        const [name = null, value = null] = attribute.split('=')
        viewportAttributes[name.trim()] = value
    })

    // This forces the viewport meta to explicitly set the viewport's height
    // It prevents viewport resizing when keyboard is shown on android tablet
    viewportAttributes.height = window.innerHeight

    // Reconstruct the comma seperated string with key=value format
    const defaults = Object.keys(viewportAttributes).map(function(attributeKey){
        return attributeKey + "=" + viewportAttributes[attributeKey]
    }).join(", ")

    // Define our viewport meta values
    var viewports = {
        default: defaults,
        desktop: 'width=1024'
    };

    // Change the viewport value based on media query
    var viewportSet = function () {
        if (window.matchMedia('only screen and (min-width: 600px) and (orientation: portrait), (min-width: 897px) and (orientation: landscape)').matches) { viewportMeta.setAttribute('content', viewports.desktop); } else { viewportMeta.setAttribute('content', viewports.default); }
    };

    // Set the correct viewport value
    viewportSet();

    // Set the correct viewport after device orientation change or resize
    window.onresize = function () {
        viewportMeta.setAttribute('content', viewports.default);
        viewportSet();
    };
}());

var browserNavigatorHelper = (function () {

  var appleFlag;
  var iosFlag;
  var macSafariFlag;
  var iosPrivateFlag;
  var iosVersion;

  function isAppleOS () {
    if (typeof appleFlag === 'undefined') {
      appleFlag = (
        !window.MSStream &&
        ('userAgent' in navigator) &&
        (/Mac|iPhone|iPad|iPod/).test(navigator.userAgent)
      )
    }
    return appleFlag 
  }

  function isIOS () {
    if (typeof iosFlag === 'undefined') {
      iosFlag = (
        !window.MSStream &&
        ('userAgent' in navigator) &&
        (/iPhone|iPad|iPod/).test(navigator.userAgent)
      )
    }
    return iosFlag
  }

  function isIE() {
   return  /*@cc_on!@*/false || !!document.documentMode;
  }

  function isEdge() {
   return  !isIE() && !!window.StyleMedia;
  }


  function isMacSafari () {
    if (typeof macSafariFlag === 'undefined') {
      macSafariFlag = (
        !isIOS() &&
        ('vendor' in navigator) &&
        navigator.vendor.indexOf('Apple') !== -1 &&
        ('userAgent' in navigator) &&
        navigator.userAgent.indexOf('CriOS') === -1 &&
        navigator.userAgent.indexOf('FxiOS') === -1
      )
    }
    return macSafariFlag
  }

  function isIOSPrivateMode () {
    if (typeof iosPrivateFlag === 'undefined') {
      iosPrivateFlag = false
      // iOS 11+
      if (window.openDatabase) {
        try {
          window.openDatabase(null, null, null, null)
        } catch (e) {
          iosPrivateFlag = true
        }
      }
      // iOS < 11
      if (!iosPrivateFlag && window.localStorage) {
        try {
          window.localStorage.setItem('testPrivate', 1)
          window.localStorage.removeItem('testPrivate')
        } catch (e) {
          iosPrivateFlag = true
        }
      }
    }
    return iosPrivateFlag
  }

  function getIOSVersion () {
    if (typeof iosVersion === 'undefined') {
      if ((/iP(hone|od|ad)/).test(navigator.platform)) {
        var iosVersion = (navigator.appVersion).match(/OS (\d+)_(\d+)_?(\d+)?/)
        var separateVersionPeacesArray = [parseInt(iosVersion[1], 10), parseInt(iosVersion[2], 10), parseInt(iosVersion[3] || 0, 10)]
        iosVersion = 'IOS ' + separateVersionPeacesArray.join('.')
      } else {
        iosVersion = null
      }
    }
    return iosVersion
  }

  function isIOSPrivateExclusiveVersion (list) {
    var listArr = list.split(',')
    return !!~listArr.indexOf(getIOSVersion())
  }

  return {
    isAppleOS: isAppleOS,
    isIOS: isIOS,
    isIE: isIE,
    isEdge: isEdge,
    isMacSafari: isMacSafari,
    isIOSPrivateMode: isIOSPrivateMode,
    getIOSVersion: getIOSVersion,
    isIOSPrivateExclusiveVersion: isIOSPrivateExclusiveVersion
  }
}())

