	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery();
			var photoHeight = $("#hp-slideshow").height();
			$("#hp-schools").css("height", photoHeight);
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			photoGallery();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			photoGallery();
		}
	}

	function photoGallery(){
		if(!$("#sw-content-container11.region.ui-hp .rs-gallery-container").length){
			// PHOTO GALLERY CONFIGURATION
			$("#sw-content-container11.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
				"imageWidth"		: 618,
				"imageHeight"		: 277,
				"linkedElement" 	: ["all"], // ["image", "title", "all"]
				"appHeader"         	: "no",
				"titles"		: "yes",
				"captions"		: "yes",
				"playPause"		: "no",
				"mainControls"		: "yes",
				"bullets"		: "yes",
				"bulletControls"		: "no",
				"hoverPause"		: "yes",
				"fullScreenVideo"	: "yes",
        			"transitionType"		: "fade", // fade or slide
				"transitionSpeed"	: "1",
				"transitionDelay"	: "5",
				"onImageLoad"		: function(){ 
								var ssWidth = $(".rs-gallery-container").width();
								var bcWidth = $(".rs-gallery-bullets-outer").width();
								var bcDiff = (ssWidth - bcWidth);
								var bcOffset = (bcDiff/2);
								$("#hp-slideshow .rs-gallery-bullets-outer").css("left",bcOffset);

				},
				"onTransitionStart"	: function(){ },
				"onTransitionEnd"	: function(){ },
				"watchVidClick"		: function(){ },
				"closeVidClick"		: function(){ },
				"allLoaded"		: function(){ 
								var getWidth = $(window).width();
								if (getWidth < 1024) { // 960+
									var galleryHeight = $("#hp-slideshow .rs-gallery-images").height() - 25;
									$("#hp-slideshow .rs-gallery-container .rs-gallery-bullets-outer").css("top",galleryHeight);


								}
									$(window).resize(function(){
														var ssWidth = $(".rs-gallery-container").width();
											var bcWidth = $(".rs-gallery-bullets-outer").width();
											var bcDiff = (ssWidth - bcWidth);
											var bcOffset = (bcDiff/2);
											$("#hp-slideshow .rs-gallery-bullets-outer").css("left",bcOffset);
											var galleryHeight = $("#hp-slideshow .rs-gallery-images").height() - 25;
				                																				$("#hp-slideshow .rs-gallery-container .rs-gallery-bullets-outer").css("top","auto");
				                																				$("#hp-slideshow .rs-gallery-container .rs-gallery-bullets-outer").css("bottom","auto");
				                																						$("#hp-slideshow .rs-gallery-container .rs-gallery-bullets-outer").css("top",galleryHeight);
		               					 	});
				}
			});

		}	
	}

