        }

	function dynamicContent(){

		// ********** MYSTART ********** //

		$("#gb-search").append($("#sw-mystart-search"));
		var signInStructure =	"
" +
						"" +
							"
Sign in to Schoolwires" +
							"
Sign in to myHISD" +
						"" +
					"";

		$("#houston-links .gb-access").append(signInStructure);
		//$("#gb-channel-list-outer .gb-access").append(signInStructure);
		//$("#sw-mystart-right .sw-mystart-button.signin").clone().addClass("hide480").appendTo("#gb-mystart-right");
		//$("#sw-mystart-right .sw-mystart-button.signin").clone().addClass("hide960 show480").appendTo("#gb-channel-list-outer .gb-access");


		//DOC replace mystart dropdown arrows
		$(".sw-myaccount-chevron").text("▼");
		$(".sw-dropdown-arrow").text("▼");

		// ********** HEADER ********** //
		if ("hisd" == "hisd" || "hisd" == "maryville"){
			$(".gb-translate a.translate.english").hide();
			$("a.footer.spanish").hide();
			$("a.footer.vietnamese").hide();
			$("a.footer.arabic").hide();
		}
		if ("hisd" == "spanish"){
			$(".gb-translate a.translate.spanish").hide();
			$("a.footer.english").hide();
			$("a.footer.vietnamese").hide();
			$("a.footer.arabic").hide();
		}
		if ("hisd" == "vietnamese"){
			$(".gb-translate a.translate.viet").hide();
			$("a.footer.spanish").hide();
			$("a.footer.english").hide();
			$("a.footer.arabic").hide();


		}
		if ("0" == "40260"){
			$(".gb-translate a.translate.arabic").hide();
			$(".gb-translate a.translate.english").show();
			$(".gb-translate a.translate.viet .divider").hide();
			$("#gb-channel-list-outer").hide();
			$('head').append('');
			$("a.footer.arabic").show();
			$("a.footer.english").hide();

		}

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");
		var channelHome = "7"
		var channelCalendar = "8"

		$("ul.sw-channel-list").prepend(channelCalendar);
		$("ul.sw-channel-list").prepend(channelHome);

		// ********** BODY ********** //
		//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").text("Home");
		//DOC replace more-link text
		$("a.more-link span").text("More News ▶");
		//DOC replace file-library more-link text
		$(".ui-widget.app.file-library a.more-link span").text("More ▶");
		//DOC auto-focus username signin field
		$("#swsignin-txt-username").focus();
		//DOC center signed out message and signin button
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        		//DOC add signed out breadcrumb
        		$(".ui-breadcrumbs").append("
Signed Out");
        	}

        	//DOC add sign in breadcrumb
        	if($("#swlogin").length){
        		$(".ui-breadcrumbs").append("
Sign In");
        	}
        	//MOD EVENTS
		$(".upcomingevents").modEvents({
			columns:"yes",
			monthLong:"no"
		});
		eventsByDay(".upcomingevents .ui-articles");
		$("a.view-calendar-link span").text("More Events ▶");

		//SHOW ALERT BOX IF ANNOUNCEMENT IS ACTIVE
		if($("#hp-alert .announcements li").size()) {
			$("#hp-alert-outer").removeClass("hidden");
		} else {
			$("#hp-slideshow-outer").css("margin-top","10px");
		}

		$("#hp-content-top .ui-widget.app:eq(0)").addClass("one");
		$("#hp-content-top .ui-widget.app:eq(1)").addClass("two");
		$("#hp-content-top .ui-widget.app:eq(2)").addClass("three");



		//ALIGN THE HEADLINE ARTICLES
		$("
").insertAfter($("#sw-content-container10.ui-hp .ui-widget.app.headlines .ui-articles li:nth-child(even)"));

		// LIMIT APPS TO 3 MAX ON HOMEPAGE HP_CONTENT TOP CONTAINER
		$("#hp-content-top .hp.column.one .ui-widget.app:gt(2)").remove();

		// APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
		$("div.ui-widget.app .ui-widget-detail img")
			.not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
			.not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
			.each(function() {
				if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
